import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const [address, setAddress] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cardDetails, setCardDetails] = useState({ number: "", date: "", cvc: "" });
  const [paypalEmail, setPaypalEmail] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = async () => {
    alert("Paiement effectué avec succès ! Votre commande arrivera le " + getDeliveryDate());
    
    // Réduction du stock en base de données
    try {
      await axios.post("http://localhost:5000/updateStock", { cart });
      setCart([]);
      localStorage.removeItem("cart");
    } catch (error) {
      console.error("Erreur lors de la mise à jour du stock :", error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-gray-900 text-white p-4 fixed top-0 w-full z-10 flex justify-between items-center">
        <nav className="flex gap-4">
          <Link to="/"><Icon icon="mdi:home" width="32" color={location.pathname === "/" ? "red" : "white"} /></Link>
          <Link to="/products"><Icon icon="mdi:shopping" width="32" color={location.pathname === "/products" ? "red" : "white"} /></Link>
          <Link to="/cart"><Icon icon="mdi:cart" width="32" color={location.pathname === "/cart" ? "red" : "white"} /></Link>
        </nav>
        {user ? (
          <div className="flex items-center">
            <span className="mr-4">{user.name}</span>
            <button onClick={() => { localStorage.removeItem("user"); setUser(null); navigate("/login"); }} className="bg-red-500 px-3 py-1 rounded">Déconnexion</button>
          </div>
        ) : (
          <button onClick={() => navigate("/login")} className="bg-blue-500 px-3 py-1 rounded">Login</button>
        )}
      </header>

      <main className="mt-16 p-4">
        <h2 className="text-3xl font-bold text-center mb-6">🛒 Mon Panier</h2>
        {cart.length === 0 ? (
          <p className="text-xl text-center">Votre panier est vide.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
            {cart.map((product, index) => (
              <div key={index} className="border p-4 bg-white shadow-lg rounded text-center">
                <img src={product.image} alt={product.name} className="w-full h-40 object-cover mb-2" />
                <h3 className="text-lg font-bold">{product.name}</h3>
                <p>Prix : {product.price}€</p>
                <button onClick={() => removeFromCart(index)} className="bg-red-500 text-white px-3 py-1 rounded mt-2">Supprimer</button>
              </div>
            ))}
          </div>
        )}

        {cart.length > 0 && (
          <div className="mt-6 p-4 bg-white shadow-lg rounded w-full max-w-md mx-auto">
            <h3 className="text-xl font-bold mb-4">💳 Paiement</h3>
            <label className="block mb-2">Adresse de livraison:</label>
            <input type="text" className="w-full p-2 border rounded mb-4" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Votre adresse complète" />

            <label className="block mb-2">Mode de paiement:</label>
            <select className="w-full p-2 border rounded mb-4" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
              <option value="card">Carte bancaire</option>
              <option value="paypal">PayPal</option>
            </select>

            {paymentMethod === "card" && (
              <>
                <input type="text" className="w-full p-2 border rounded mb-4" value={cardDetails.number} onChange={(e) => setCardDetails({ ...cardDetails, number: e.target.value })} placeholder="Numéro de carte" />
                <input type="text" className="w-full p-2 border rounded mb-4" value={cardDetails.date} onChange={(e) => setCardDetails({ ...cardDetails, date: e.target.value })} placeholder="Date d'expiration" />
                <input type="text" className="w-full p-2 border rounded mb-4" value={cardDetails.cvc} onChange={(e) => setCardDetails({ ...cardDetails, cvc: e.target.value })} placeholder="CVC" />
              </>
            )}

            {paymentMethod === "paypal" && (
              <input type="email" className="w-full p-2 border rounded mb-4" value={paypalEmail} onChange={(e) => setPaypalEmail(e.target.value)} placeholder="Email PayPal" />
            )}

            <p className="mb-4">📅 Date de livraison prévue: <strong>{getDeliveryDate()}</strong></p>

            <button onClick={handlePayment} className="w-full bg-green-500 text-white p-3 rounded">Effectuer le paiement</button>
          </div>
        )}
      </main>
    </div>
  );
};

export default CartPage;
