import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./ProductsPage.css";
import userIcon from "../assets/user-icon.png"; // Assurez-vous d'avoir une icône utilisateur appropriée

const ProductsPage = () => {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <div className="products-page">
      <div className="header">
        <div className="user-menu-container">
          <img
            src={userIcon}
            alt="User"
            className="user-icon"
            onClick={toggleMenu}
          />
          {showMenu && (
            <div className="dropdown-menu">
              <Link to="/login">Login</Link>
              <Link to="/account">Compte</Link>
              <Link to="/orders">Commandes</Link>
              <Link to="/support">Assistance</Link>
              <Link to="/wallet">Portefeuille</Link>
              <Link to="/addresses">Adresses</Link>
              <button onClick={() => console.log("Déconnexion")}>Déconnexion</button>
            </div>
          )}
        </div>
      </div>
      <h1>Nos Produits</h1>
      <div className="products-grid">
        {/* Affichage des produits ici */}
      </div>
    </div>
  );
};
