import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

const ProductPage = () => {
  const [user, setUser] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  // Récupération des infos utilisateur
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <div style={{
      backgroundImage: `url('/assets/marvelstore.webp')`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      textAlign: "center",
      padding: "20px",
      color: "#fff"
    }}>
      {/* 🔹 Barre de navigation avec icône utilisateur */}
      <div className="flex justify-between items-center bg-white shadow-md p-4 w-full fixed top-0 left-0 z-50">
        <h1 className="text-2xl font-bold">🛍️ Produits</h1>

        {/* ✅ Profil utilisateur */}
        <div className="relative">
          <button onClick={() => setMenuOpen(!menuOpen)} className="flex items-center space-x-2">
            <img
              src="https://cdn-icons-png.flaticon.com/512/847/847969.png"
              alt="User"
              className="w-8 h-8 rounded-full border border-gray-300"
            />
            {user && <span className="font-semibold">{user.name}</span>}
          </button>

          {/* 🔻 Menu déroulant */}
          {menuOpen && (
            <div className="absolute right-0 mt-2 w-56 bg-white shadow-lg rounded-lg overflow-hidden z-50">
              <ul className="py-2">
                {!user ? (
                  <li>
                    <Link to="/login" className="block px-4 py-2 hover:bg-gray-100">🔐 Login</Link>
                  </li>
                ) : (
                  <>
                    <li>
                      <Link to="/account" className="block px-4 py-2 hover:bg-gray-100">👤 Compte</Link>
                    </li>
                    <li>
                      <Link to="/orders" className="block px-4 py-2 hover:bg-gray-100">📦 Commandes</Link>
                    </li>
                    <li>
                      <Link to="/support" className="block px-4 py-2 hover:bg-gray-100">📞 Assistance Client</Link>
                    </li>
                    <li>
                      <Link to="/wallet" className="block px-4 py-2 hover:bg-gray-100">💳 Portefeuille</Link>
                    </li>
                    <li>
                      <Link to="/addresses" className="block px-4 py-2 hover:bg-gray-100">🏠 Adresses</Link>
                    </li>
                    <li>
                      <button onClick={handleLogout} className="block px-4 py-2 text-red-500 hover:bg-gray-100 w-full text-left">
                        🚪 Déconnexion
                      </button>
                    </li>
                  </>
                )}
              </ul>
            </div>
          )}
        </div>
      </div>

      {/* 🛍️ Liste des produits */}
      <div className="p-6 mt-16">
        <p>📢 Ici, la liste des produits...</p>
      </div>
    </div>
  );
};

export default ProductPage;
