import React, { useState } from "react";

const AddressesPage = () => {
  const [country, setCountry] = useState("");
  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [address, setAddress] = useState("");
  const [deliveryName, setDeliveryName] = useState("");
  const [zipCode, setZipCode] = useState("");
  const [city, setCity] = useState("");
  const [province, setProvince] = useState("");
  const [consent, setConsent] = useState(false);
  const [defaultAddress, setDefaultAddress] = useState(false);
  const [showInstructions, setShowInstructions] = useState(false);
  const [securityCode, setSecurityCode] = useState("");
  const [intercom, setIntercom] = useState("");
  const [deliverOnSaturday, setDeliverOnSaturday] = useState("yes");
  const [deliverOnSunday, setDeliverOnSunday] = useState("yes");
  const [details, setDetails] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!country || !fullName || !phoneNumber || !address || !deliveryName || !zipCode || !city || !province || !consent) {
      alert("Veuillez remplir tous les champs obligatoires et consentir à l'utilisation de l'adresse.");
      return;
    }
    alert("Adresse enregistrée avec succès !");
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/marvelstorewallet.jpeg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
    <div style={{ maxWidth: "500px", margin: "auto", textAlign: "left" }}>
      <h2>🏠 Mes Adresses</h2>
      <p>Ajoutez ou modifiez vos adresses de livraison.</p>
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <select value={country} onChange={(e) => setCountry(e.target.value)} required>
          <option value="">Sélectionnez un pays</option>
          <option value="Italie">Italie</option>
          <option value="Allemagne">Allemagne</option>
          <option value="France">France</option>
          <option value="Belgique">Belgique</option>
          <option value="Suisse">Suisse</option>
          <option value="Canada">Canada</option>
          <option value="Cameroun">Cameroun</option>
          <option value="Cote d'Ivoire">Cote d'Ivoire</option>
          <option value="Gabon">Gabon</option>
          <option value="Congo">Congo</option>
        </select>
        <input type="text" placeholder="Nom et prénom" value={fullName} onChange={(e) => setFullName(e.target.value)} required />
        <input type="tel" placeholder="Numéro de téléphone" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} required />
        <input type="text" placeholder="Adresse" value={address} onChange={(e) => setAddress(e.target.value)} required />
        <input type="text" placeholder="Nom pour le livreur" value={deliveryName} onChange={(e) => setDeliveryName(e.target.value)} required />
        <input type="text" placeholder="Code postal" value={zipCode} onChange={(e) => setZipCode(e.target.value)} required />
        <input type="text" placeholder="Ville" value={city} onChange={(e) => setCity(e.target.value)} required />
        <input type="text" placeholder="Province" value={province} onChange={(e) => setProvince(e.target.value)} required />
        
        <label>
          <input type="checkbox" checked={consent} onChange={() => setConsent(!consent)} required />
          J'accepte que cette adresse soit utilisée pour la livraison
        </label>
        <label>
          <input type="checkbox" checked={defaultAddress} onChange={() => setDefaultAddress(!defaultAddress)} />
          Définir cette adresse comme adresse par défaut
        </label>
        
        <button type="button" onClick={() => setShowInstructions(!showInstructions)}>Ajouter des instructions de livraison</button>
        
        {showInstructions && (
          <div style={{ marginTop: "10px" }}>
            <input type="text" placeholder="Code de sécurité" value={securityCode} onChange={(e) => setSecurityCode(e.target.value)} />
            <input type="text" placeholder="Interphone (nom affiché au livreur)" value={intercom} onChange={(e) => setIntercom(e.target.value)} />
            <label>
              Livraison le samedi:
              <select value={deliverOnSaturday} onChange={(e) => setDeliverOnSaturday(e.target.value)}>
                <option value="yes">Oui</option>
                <option value="no">Non</option>
              </select>
            </label>
            <label>
              Livraison le dimanche:
              <select value={deliverOnSunday} onChange={(e) => setDeliverOnSunday(e.target.value)}>
                <option value="yes">Oui</option>
                <option value="no">Non</option>
              </select>
            </label>
            <textarea placeholder="Détails supplémentaires" value={details} onChange={(e) => setDetails(e.target.value)}></textarea>
          </div>
        )}
        
        <button type="submit">Enregistrer l'adresse</button>
      </form>
      </div>
    </div>
  );
};

export default AddressesPage;
