import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("üü° Donn√©es envoy√©es :", { email, password }); // Ajoute ceci

    if (!email || !password) {
      setError("Veuillez remplir tous les champs !");
      return;
    }

    console.log("üü¢ Donn√©es envoy√©es au backend :", { email, password });

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });
      alert("Connexion r√©ussie !");
      console.log("üü¢ R√©ponse du serveur :", response.data);

      // ‚úÖ Sauvegarde de l'utilisateur dans localStorage
      localStorage.setItem("user", JSON.stringify(response.data.user));

      // ‚úÖ Redirection vers la page des produits apr√®s connexion
      navigate("/");
    } catch (error) {
      console.error("‚ùå Erreur :", error.response?.data?.message || "Probl√®me serveur");
      setError(error.response?.data?.message || "Erreur lors de la connexion.");
    }
  };

  return (
    <div>
      {/* Liens en haut √† droite */}
      <div className="absolute top-4 right-4 flex gap-4">
        <Link to="/" className="text-white text-lg font-bold hover:underline">
          Accueil
        </Link>
        <Link to="/products" className="text-white text-lg font-bold hover:underline">
          Produits
        </Link>
      </div>

      {/* Page de connexion */}
      <div style={{
        backgroundImage: `url('/assets/marvelstore.jpg')`, // ‚úÖ Chemin corrig√©
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
        <h2>Se connecter</h2>
        {error && <p style={{ color: "red" }}>{error}</p>}

        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column" }}>
          <input 
            type="email" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            className="p-3 mb-4 rounded bg-white text-black"
            required 
          />
          
          <input 
            type="password" 
            placeholder="Mot de passe" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            className="p-3 mb-4 rounded bg-white text-black"
            required 
          />
          
          <button type="submit">Se connecter</button>
        </form>

        {/* üîπ Lien vers "Mot de passe oubli√©" */}
        <div className="text-center mt-4">
          <Link to="/forgot-password" className="text-blue-500 hover:underline">
            üîë Mot de passe oubli√© ?
          </Link>
        </div>

        <p>Pas encore de compte ? <Link to="/register">Cr√©er un compte</Link></p>
      </div>
    </div>
  );
};

export default LoginPage;
