import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const openModal = (product) => {
    setSelectedProduct(product);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  return (
    <div style={{ backgroundImage: "url('/assets/marvelstoreproduit.jpeg')", backgroundSize: "cover", minHeight: "100vh" }}>
      {/* 🔹 Barre supérieure */}
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", backgroundColor: "#222", color: "white", padding: "10px 20px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <h1>🛍️ Faites-vous plaisir</h1>
        <div style={{ display: "flex", alignItems: "center" }}>
          <Icon icon="mdi:account-circle" width="40" />
          {user ? (
            <div style={{ display: "flex", alignItems: "center", cursor: "pointer", marginLeft: "10px" }} onClick={() => setUserMenuOpen(!userMenuOpen)}>
              <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
              {userMenuOpen && (
                <div style={{ position: "absolute", top: "50px", right: "0", backgroundColor: "white", color: "black", padding: "10px", borderRadius: "5px", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)" }}>
                  <p><strong>Nom :</strong> {user.name}</p>
                  <p><strong>Téléphone :</strong> {user.phone}</p>
                  <button onClick={handleLogout} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px", width: "100%" }}>Déconnexion</button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" style={{ marginLeft: "10px", fontSize: "16px", textDecoration: "none", color: "white" }}>Login</Link>
          )}
        </div>
      </header>
      
      <div style={{ marginTop: "80px", padding: "20px" }}>
        <h2>🛍️ Nos Produits</h2>
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
        />
        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
          {products.filter(product => product.name.toLowerCase().includes(searchTerm.toLowerCase())).map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
              <h3>{product.name}</h3>
              <img src={product.image} alt={product.name} style={{ width: "150px", cursor: "pointer" }} onClick={() => openModal(product)} />
              <button onClick={() => openModal(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">Ajouter au panier</button>
            </div>
          ))}
        </div>
      </div>
      
      {selectedProduct && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", backgroundColor: "rgba(0,0,0,0.7)", display: "flex", justifyContent: "center", alignItems: "center" }}>
          <div style={{ backgroundColor: "white", padding: "20px", borderRadius: "10px", width: "400px" }}>
            <h2>{selectedProduct.name}</h2>
            <img src={selectedProduct.image} alt={selectedProduct.name} style={{ width: "100%" }} />
            <p>{selectedProduct.description}</p>
            <label>Couleur :</label>
            <select>
              <option>Rouge</option>
              <option>Bleu</option>
              <option>Vert</option>
              <option>Noir</option>
              <option>Blanc</option>
            </select>
            {selectedProduct.category === "clothing" && (
              <>
                <label>Taille :</label>
                <select>
                  <option>S</option>
                  <option>M</option>
                  <option>L</option>
                  <option>XL</option>
                  <option>XXL</option>
                </select>
              </>
            )}
            {selectedProduct.category === "shoes" && (
              <>
                <label>Pointure :</label>
                <select>
                  {[...Array(10)].map((_, i) => (
                    <option key={i}>{36 + i}</option>
                  ))}
                </select>
              </>
            )}
            <label>Quantité :</label>
            <input type="number" min="1" max={selectedProduct.stock} style={{ borderColor: selectedProduct.stock <= 10 ? "red" : "black" }} />
            <button onClick={closeModal} style={{ marginTop: "10px", display: "block" }}>Fermer</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
