import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const RegisterPage = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !email || !password) {
      setError("Veuillez remplir tous les champs !");
      return;
    }

    console.log("üü¢ Donn√©es envoy√©es au backend :", { name, email, password });

    try {
      const response = await axios.post("http://localhost:5000/api/users/register", { name, email, password });
      alert("Inscription r√©ussie !");
      console.log("üü¢ R√©ponse du serveur :", response.data);

      // ‚úÖ Redirection vers la page de connexion apr√®s inscription
      navigate("/products");
    } catch (error) {
      console.error("‚ùå Erreur :", error.response?.data?.message || "Probl√®me serveur");
      setError(error.response?.data?.message || "Erreur lors de l'inscription.");
    }
  };

  return (
    <div style={{
        //backgroundColor: "red",
      backgroundImage: `url('/assets/marvelstore.webp')`, // ‚úÖ V√©rifie bien ce chemin
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      textAlign: "center",
      padding: "20px",
      color: "#fff"
        }}>
      <h2>Cr√©er un compte</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column" }}>
        <input 
          type="text" 
          placeholder="Nom" 
          value={name} 
          onChange={(e) => setName(e.target.value)} 
          required 
        />
        
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
        
        <input 
          type="password" 
          placeholder="Mot de passe" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />

<input 
          type="number" 
          placeholder="Num√©ro" 
          value={number} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
        
        <button type="submit">S'inscrire</button>
      </form>

      <p>D√©j√† un compte ? <a href="/login">Se connecter</a></p>
    </div>
  );
};

export default RegisterPage;
