import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const SupportPage = () => {
  const [request, setRequest] = useState("");
  const navigate = useNavigate();

  const handleSubmit = () => {
    alert("Votre requête a été envoyée !");
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/marvelstoreassitance.jpeg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2>📞 Assistance Client</h2>
      <p><strong>Numéro d'aide :</strong> +393792182036</p>
      <p><strong>Email :</strong> rngasseu@gmail.com</p>
      
      <h3>❓ Foire Aux Questions (FAQ)</h3>
      <details>
        <summary>🔄 Comment changer mes informations personnelles ?</summary>
        <p>Rendez-vous sur la page "Mon compte", puis modifiez votre nom, email, mot de passe ou numéro de téléphone.</p>
      </details>
      <details>
        <summary>📦 Comment voir et gérer mes commandes ?</summary>
        <p>Accédez à la page "Commandes" pour consulter vos achats, suivre vos livraisons ou racheter un produit.</p>
      </details>
      <details>
        <summary>💳 Comment consulter et modifier mon portefeuille ?</summary>
        <p>Sur la page "Portefeuille", vous pouvez ajouter, modifier ou supprimer vos cartes bancaires et comptes PayPal.</p>
      </details>
      <details>
        <summary>🏠 Comment voir et modifier mon adresse ?</summary>
        <p>Dans la section "Adresses", ajoutez, modifiez ou supprimez vos adresses de livraison et facturation.</p>
      </details>
      <details>
        <summary>🚪 Comment se déconnecter ?</summary>
        <p>Cliquez sur l'icône du profil en haut à droite et sélectionnez "Déconnexion".</p>
      </details>
      <details>
        <summary>🛒 Comment acheter un article ?</summary>
        <p>Ajoutez des produits à votre panier, puis passez à la caisse pour finaliser votre commande.</p>
      </details>
      <details>
        <summary>🗑 Comment supprimer mon compte ?</summary>
        <p>Envoyez un email à rngasseu@gmail.com en demandant la suppression de votre compte.</p>
      </details>
      
      <h3>📩 Envoyer une requête</h3>
        <label>Votre message :</label>
        <textarea 
          value={request} 
          onChange={(e) => setRequest(e.target.value)} 
          style={{ width: "100%", height: "100px", marginTop: "10px" }}
        />
        <button
          onClick={() => {
            const subject = encodeURIComponent("Requête client");
            const body = encodeURIComponent(request);
            window.location.href = `mailto:marvelstore1010@gmail.com?subject=${subject}&body=${body}`;
          }}
          style={{ marginTop: "10px" }}
        >
          Envoyer
        </button>

        <div
        style={{
          backgroundColor: "rgba(0,0,0,0.7)",
          padding: "20px",
          borderRadius: "10px",
          width: "80%",
          maxWidth: "400px",
          marginTop: "20px",
        }}
      >
        <h3>📌 Options du compte</h3>
        <p><a href="/account">👤 Compte</a></p>
        <p><a href="/orders">📦 Mes Commandes</a></p>
        <p><a href="/wallet">💳 Mon Portefeuille</a></p>
        <p><a href="/addresses">🏠 Mes Adresses</a></p>
      </div>
    
      <button onClick={() => navigate("/products")} style={{ marginTop: "10px", backgroundColor: "gray" }}>
        ⬅ Retour aux Produits
      </button>
    </div>
  );
};

export default SupportPage;
