import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError("Veuillez remplir tous les champs !");
      return;
    }

    console.log("üü¢ Donn√©es envoy√©es au backend :", { email, password });

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });
      alert("Connexion r√©ussie !");
      console.log("üü¢ R√©ponse du serveur :", response.data);

      // ‚úÖ Redirection vers la page des produits apr√®s connexion
      navigate("/products");
    } catch (error) {
      console.error("‚ùå Erreur :", error.response?.data?.message || "Probl√®me serveur");
      setError(error.response?.data?.message || "Erreur lors de la connexion.");
    }
  };

  return (
    <div style={{
      backgroundImage: `url('/assets/marvelstore.jpg')`,
      backgroundSize: "cover",
      //backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      //minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      //alignItems: "center",
      //justifyContent: "center",
      //textAlign: "center",
      padding: "20px",
      color: "#fff"
    }}>
      
      {/* üîπ Liens en haut √† droite */}
      <div className="absolute top-4 right-4 flex space-x-4">
        <Link to="/" className="text-white text-lg font-bold hover:underline">
          Accueil
        </Link>
        <Link to="/products" className="text-white text-lg font-bold hover:underline">
          Produits
        </Link>
      </div>

      <h2 className="text-2xl font-bold mb-4">Se connecter</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}

      <form onSubmit={handleSubmit} className="flex flex-col items-center bg-white p-6 rounded-lg shadow-md">
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          className="p-3 mb-4 w-64 rounded bg-gray-200 text-black"
          required 
        />
        
        <input 
          type="password" 
          placeholder="Mot de passe" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          className="p-3 mb-4 w-64 rounded bg-gray-200 text-black"
          required 
        />
        
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700">
          Se connecter
        </button>
      </form>

      {/* üîπ Lien vers "Mot de passe oubli√©" */}
      <div className="text-center mt-4">
        <Link to="/forgot-password" className="text-blue-500 hover:underline">
          üîë Mot de passe oubli√© ?
        </Link>
      </div>

      <p className="mt-4">Pas encore de compte ? 
        <Link to="/register" className="text-blue-500 hover:underline ml-1">
          Cr√©er un compte
        </Link>
      </p>
    </div>
  );
};

export default LoginPage;
