import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom"; // ‚úÖ Ajout de Link ici
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    console.log("üöÄ Bouton Entrer cliqu√©, tentative de connexion...");

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", {
        email,
        password
      });

      console.log("üü¢ R√©ponse serveur :", response.data);

      if (response.data.success) {
        alert("Connexion r√©ussie !");
        navigate("/products"); // ‚úÖ Redirection vers la page des produits
      } else {
        setError("‚ùå Identifiants incorrects.");
      }
    } catch (error) {
      console.error("‚ùå Erreur :", error.response?.data?.message || "Probl√®me serveur");
      setError("Erreur de connexion.");
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-1/3">
        <h2 className="text-center text-2xl font-bold mb-6">Connexion</h2>
        {error && <p className="text-red-500 text-center">{error}</p>}

        <form onSubmit={handleSubmit}>
          <input
            type="email"
            className="w-full p-4 mb-4 border rounded-lg"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            className="w-full p-4 mb-4 border rounded-lg"
            placeholder="Mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="w-full bg-green-500 text-white p-4 rounded-lg">
            Entrer
          </button>
        </form>

        {/* üîπ Lien vers "Mot de passe oubli√©" */}
        <div className="text-center mt-4">
          <Link to="/forgot-password" className="text-blue-500 hover:underline">
            üîë Mot de passe oubli√© ?
          </Link>
        </div>

        {/* üîπ Lien vers "S'inscrire" */}
        <div className="text-center mt-4">
          <span className="text-gray-600">Pas encore inscrit ? </span>
          <Link to="/register" className="text-blue-500 font-bold hover:underline">
            ‚úçÔ∏è S'inscrire
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
