import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [menuOpen, setMenuOpen] = useState(false);
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const addToCart = (product) => {
    const updatedCart = [...cart, {
      _id: product._id,
      name: product.name,
      price: product.price,
      image: product.image
    }];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    alert(`${product.name} a été ajouté au panier !`);
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      {/* 🔹 Barre supérieure + Navigation */}
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 20px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        {/* 🔹 Barre latérale en haut */}
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          <Link to="/" className="nav-link"><Icon icon="mdi:home" width="32" /></Link>
          <Link to="/products" className="nav-link"><Icon icon="mdi:shopping" width="32" /></Link>
          <Link to="/cart" className="nav-link"><Icon icon="mdi:cart" width="32" /></Link>
          <Link to="/account" className="nav-link"><Icon icon="mdi:account" width="32" /></Link>
          <Link to="/wallet" className="nav-link"><Icon icon="mdi:credit-card" width="32" /></Link>
          <Link to="/addresses" className="nav-link"><Icon icon="mdi:map-marker" width="32" /></Link>
        </nav>
        
        {/* 🔹 Barre supérieure */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <h1>🛍️ Produits</h1>
          
          {/* 🔹 Icône utilisateur */}
          {user && (
            <div style={{ display: "flex", alignItems: "center", cursor: "pointer", position: "relative" }} onClick={() => setUserMenuOpen(!userMenuOpen)}>
              <div style={{ position: "relative" }}>
                <Icon icon="mdi:account-circle" width="40" />
                <span style={{ position: "absolute", bottom: "0", right: "0", backgroundColor: "green", width: "10px", height: "10px", borderRadius: "50%" }}></span>
              </div>
              <span style={{ marginLeft: "10px" }}>{user.name}</span>

              {userMenuOpen && (
                <div style={{ position: "absolute", top: "50px", right: "0", backgroundColor: "white", color: "black", padding: "10px", borderRadius: "5px", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)" }}>
                  <p><strong>Nom :</strong> {user.name}</p>
                  <p><strong>Téléphone :</strong> {user.phone}</p>
                  <button onClick={handleLogout} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px", width: "100%" }}>Déconnexion</button>
                </div>
              )}
            </div>
          )}
        </div>
      </header>
      
      <div style={{ marginTop: "120px", flex: 1, padding: "20px" }}>
        <h2>🛍️ Faite plaisir à vos proches mais aussi plus à vous</h2>
        
        {/* 🔍 Champ de recherche */}
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
        />

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
          {filteredProducts.map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}€</p>
              {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
              <button onClick={() => addToCart(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
                Ajouter au panier
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductsPage;
