import React, { useState, useEffect } from "react";
import axios from "axios";

const WalletPage = () => {
  const [cards, setCards] = useState([]);
  const [newCard, setNewCard] = useState({
    number: "",
    expiryMonth: "",
    expiryYear: "",
    cvc: "",
    holder: ""
  });
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    if (user) {
      axios.get(`http://localhost:5000/api/users/${user.email}/cards`)
        .then(response => setCards(response.data))
        .catch(error => console.error("Erreur lors du chargement des cartes:", error));
    }
  }, [user]);

  const handleChange = (e) => {
    setNewCard({ ...newCard, [e.target.name]: e.target.value });
  };

  const addCard = async () => {
    if (!user) {
      alert("Veuillez vous connecter pour ajouter une carte.");
      return;
    }
    try {
      const response = await axios.post(`http://localhost:5000/api/users/${user.email}/cards`, newCard);
      setCards([...cards, response.data]);
      setNewCard({ number: "", expiryMonth: "", expiryYear: "", cvc: "", holder: "" });
      alert("Carte ajoutÃ©e avec succÃ¨s !");
    } catch (error) {
      console.error("Erreur lors de l'ajout de la carte:", error);
    }
  };

  return (
    <div>
      <h2>ðŸ’³ Mon Portefeuille</h2>
      <h3>Cartes bancaires</h3>
      {cards.map((card, index) => (
        <p key={index}>
          **** **** **** {card.number.slice(-4)} - Exp: {card.expiryMonth}/{card.expiryYear} - {card.holder}
        </p>
      ))}

      <div>
        <input type="text" name="number" value={newCard.number} onChange={handleChange} placeholder="NumÃ©ro de carte" maxLength="16" />
        <input type="text" name="expiryMonth" value={newCard.expiryMonth} onChange={handleChange} placeholder="MM" maxLength="2" />
        <input type="text" name="expiryYear" value={newCard.expiryYear} onChange={handleChange} placeholder="YY" maxLength="2" />
        <input type="text" name="cvc" value={newCard.cvc} onChange={handleChange} placeholder="CVC" maxLength="4" />
        <input type="text" name="holder" value={newCard.holder} onChange={handleChange} placeholder="Nom et prÃ©nom" />
        <button onClick={addCard}>Ajouter</button>
      </div>
    </div>
  );
};

export default WalletPage;
