import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));

    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const addToCart = (product) => {
    const updatedCart = [...cart, {
      _id: product._id,
      name: product.name,
      price: product.price,
      image: product.image
    }];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    alert(`${product.name} a √©t√© ajout√© au panier !`);
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/marvelstoreproduits.jpeg')`,
        backgroundSize: "cover",
        //backgroundPosition: "center",
        //backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        //display: "flex",
        flexDirection: "column",
        //alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      
      {/* üîπ Barre de navigation avec ic√¥ne utilisateur */}
<div className="flex justify-between items-center bg-white shadow-md p-4 w-full fixed top-0 left-0 z-50">
    <h1 className="text-2xl font-bold">Profil</h1>

    {/* ‚úÖ Profil utilisateur align√© √† droite */}
    <div className="relative ml-auto">
    <button onClick={() => setMenuOpen(!menuOpen)} className="flex items-center space-x-2">
        <img
        src="https://cdn-icons-png.flaticon.com/512/847/847969.png"
        alt="User"
        className="w-6 h-6 rounded-full border border-gray-300"
        />
    </button>

    {/* üîª Menu d√©roulant */}
    {menuOpen && (
        <div className="absolute right-0 mt-2 w-40 bg-white shadow-lg rounded-lg overflow-hidden z-50">
        <ul className="py-2">
            {!user ? (
            <li>
                <Link to="/login" className="block px-4 py-2 hover:bg-gray-100">üîê Login</Link>
            </li>
            ) : (
            <>
                <li>
                <Link to="/account" className="block px-4 py-2 hover:bg-gray-100">üë§ Compte</Link>
                </li>
                <li>
                <Link to="/orders" className="block px-4 py-2 hover:bg-gray-100">üì¶ Commandes</Link>
                </li>
                <li>
                <Link to="/support" className="block px-4 py-2 hover:bg-gray-100">üìû Assistance</Link>
                </li>
                <li>
                <Link to="/wallet" className="block px-4 py-2 hover:bg-gray-100">üí≥ Portefeuille</Link>
                </li>
                <li>
                <Link to="/addresses" className="block px-4 py-2 hover:bg-gray-100">üè† Adresses</Link>
                </li>
                <li>
                <button onClick={handleLogout} className="block px-4 py-2 text-red-500 hover:bg-gray-100 w-full text-left">
                    üö™ D√©connexion
                </button>
                </li>
            </>
            )}
        </ul>
        </div>
    )}
    </div>
</div>

<h2 className="mt-16">üõçÔ∏è Nos Produits</h2>

{/* üîç Champ de recherche */}
<input
    type="text"
    placeholder="Rechercher un produit..."
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
    style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
/>

{/* üìÑ Lien vers la page du panier */}
<Link to="/cart" className="bg-blue-500 text-white px-4 py-2 rounded">
    Voir mon panier üõí ({cart.length})
</Link>

<div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
    {filteredProducts.map((product) => (
    <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
        <h3>{product.name}</h3>
        <p>Prix : {product.price}‚Ç¨</p>
        {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
        <button onClick={() => addToCart(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
        Ajouter au panier
        </button>
    </div>
    ))}
</div>
</div>
);
};

export default ProductsPage;
