import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });

      if (response.data.success) {
        alert("Connexion réussie !");
        navigate("/dashboard"); // 🔄 Redirige vers le tableau de bord après connexion
      }
    } catch (error) {
      setError("❌ Email ou mot de passe incorrect.");
    }
  };

  return (
    <div style={{
        //backgroundColor: "red",
        backgroundImage: `url('/assets/marvelstore.webp')`, // ✅ Vérifie bien ce chemin
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      {/* 🔹 Bouton pour revenir à l'accueil */}
      <div className="absolute top-4 right-9">
        <Link to="/" className="text-white text-lg font-bold hover:underline">
          Accueil
        </Link>
      </div>

      <div className="bg-black bg-opacity-50 p-8 rounded-lg shadow-lg text-white w-full max-w-md">
        <h2 className="text-3xl font-bold text-center mb-6">Bienvenue !</h2>

        {error && <p className="text-red-500 text-center">{error}</p>}

        <form onSubmit={handleSubmit} className="flex flex-col">
          <label className="mb-2">Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-3 mb-4 rounded bg-white text-black"
            required
          />

          <label className="mb-2">Mot de passe</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="p-3 mb-4 rounded bg-white text-black"
            required
          />

          <div className="flex items-center mb-4">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
              className="mr-2"
            />
            <label>Se souvenir de moi</label>
          </div>

          <button type="submit" className="bg-green-700 hover:bg-green-800 p-3 rounded text-white font-bold">
            ENTRER
          </button>
        </form>

        {/* 🔹 Lien "Mot de passe oublié" */}
        <div className="mt-4 text-center">
          <Link to="/forgot-password" className="text-white hover:underline">
            Mot de passe oublié ?
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
