import React, { useState } from "react";
import { Link } from "react-router-dom";

const CartPage = () => {
  const [cart, setCart] = useState([]);

  // ‚ùå Supprimer un produit du panier
  const removeFromCart = (index) => {
    const newCart = [...cart];
    newCart.splice(index, 1);
    setCart(newCart);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h2 className="text-3xl font-bold text-center mb-6">üõí Mon Panier</h2>

      {cart.length === 0 ? (
        <p className="text-center text-lg">Votre panier est vide.</p>
      ) : (
        <div className="grid grid-cols-2 gap-6">
          {cart.map((product, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-md text-center">
              <h3 className="text-xl font-bold mb-2">{product.name}</h3>
              <p className="text-lg text-gray-700 mb-2">Prix : {product.price}‚Ç¨</p>
              {product.image && <img src={product.image} alt={product.name} className="w-40 mx-auto mb-4" />}
              <button 
                onClick={() => removeFromCart(index)} 
                className="bg-red-500 text-white p-3 rounded-lg hover:bg-red-600">
                ‚ùå Supprimer
              </button>
            </div>
          ))}
        </div>
      )}

      <div className="text-center mt-6">
        <Link to="/products" className="bg-blue-600 text-white p-3 rounded-lg">
          üõçÔ∏è Continuer mes achats
        </Link>
      </div>
    </div>
  );
};

export default CartPage;
