import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const CartPage = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <h2 className="text-3xl font-bold mb-6">ðŸ›’ Mon Panier</h2>

      {cart.length === 0 ? (
        <p className="text-xl">Votre panier est vide.</p>
      ) : (
        <div className="w-full max-w-lg">
          {cart.map((product, index) => (
            <div key={product._id} style={{ border: "10px", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
              <div>
                <h3 className="text-lg font-bold">{product.name}</h3>
                <p>Prix : {product.price}â‚¬</p>
                {/* âœ… Ajout de l'affichage de l'image */}
                {product.image && (
                  <img src={product.image} alt={product.name} style={{ width: "150px" }} />
                )}
              </div>
              <button
                onClick={() => removeFromCart(index)}
                className="bg-red-500 text-white px-3 py-1 rounded"
              >
                Supprimer
              </button>
            </div>
          ))}
        </div>
      )}

      <Link to="/products" className="bg-blue-500 text-white px-4 py-2 rounded mt-4">
        ðŸ”™ Retour aux produits
      </Link>
    </div>
  );
};

export default CartPage;
