import React, { useState } from "react";

const WalletPage = () => {
  const [cards, setCards] = useState([]);
  const [paypal, setPaypal] = useState([]);
  const [newCard, setNewCard] = useState("");
  const [newPaypal, setNewPaypal] = useState("");

  const addCard = () => {
    setCards([...cards, newCard]);
    setNewCard("");
  };

  const addPaypal = () => {
    setPaypal([...paypal, newPaypal]);
    setNewPaypal("");
  };

  return (
    <div>
      <h2>ðŸ’³ Mon Portefeuille</h2>
      <h3>Cartes bancaires</h3>
      {cards.map((card, index) => (
        <p key={index}>{card}</p>
      ))}
      <input type="text" value={newCard} onChange={(e) => setNewCard(e.target.value)} placeholder="Ajouter une carte" />
      <button onClick={addCard}>Ajouter</button>
      <h3>Comptes PayPal</h3>
      {paypal.map((account, index) => (
        <p key={index}>{account}</p>
      ))}
      <input type="text" value={newPaypal} onChange={(e) => setNewPaypal(e.target.value)} placeholder="Ajouter un PayPal" />
      <button onClick={addPaypal}>Ajouter</button>
    </div>
  );
};

export default WalletPage;
