import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import HomePage from "./pages/HomePage";
import AdminOrdersPage from "./pages/AdminOrdersPage";

function App() {
  return (
    <Router>
      <div
        style={{
          backgroundImage: `url('C:/Users/nwotc/Desktop/marvelstore/frontend/frontend/public/assets/background.webp.webp')`, // Correction du chemin d'image
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          color: "#fff", // Couleur du texte en blanc
          textShadow: "2px 2px 4px rgba(0, 0, 0, 0.5)", // Améliorer la lisibilité
        }}
      >
        <h1>Bienvenue sur l'application e-commerce</h1>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/admin/orders" element={<AdminOrdersPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
