import { useNavigate } from 'react-router-dom';

function HomePage() {
  const navigate = useNavigate();

  const handleViewProducts = () => {
    console.log("Redirection vers la page de login");
    navigate('/login'); // Redirige vers la page login
  };

  return (
    <div>
      <div
      style={{
        backgroundImage: 'url(/assets/background.webp)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
      }}
    ></div>
      <h1>Bienvenue sur Marvel Store</h1>
      <p>Ajoutez vos produits et profitez du shopping en ligne !</p>
      <button onClick={handleViewProducts}>Entrer dans la boutique</button>
    </div>
  );
}

export default HomePage;
