import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });

      if (response.data.success) {
        alert("Connexion réussie !");
        navigate("/dashboard"); // 🔄 Redirection après connexion
      }
    } catch (error) {
      setError("❌ Email ou mot de passe incorrect.");
    }
  };

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center bg-cover bg-center px-4"
      style={{ backgroundImage: `url('/assets/marvelstoreproduits.jpeg')` }}
    >
      {/* 🔹 Bouton Accueil en haut à droite */}
      <div className="absolute top-6 right-8">
        <Link to="/" className="text-white text-xl font-bold hover:underline">
          ⬅ Accueil
        </Link>
      </div>

      {/* 🔹 Conteneur du formulaire */}
      <div className="bg-black bg-opacity-75 p-10 rounded-lg shadow-lg text-white w-full max-w-lg">
        <h2 className="text-4xl font-bold text-center mb-8">Bienvenue !</h2>

        {/* 🔹 Affichage de l'erreur si connexion échoue */}
        {error && <p className="text-red-500 text-lg text-center mb-6">{error}</p>}

        <form onSubmit={handleSubmit} className="flex flex-col">
          {/* 🔹 Champ Email */}
          <div className="mb-6">
            <label className="block text-lg font-semibold mb-2">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-4 rounded-lg bg-white text-black text-lg"
              required
            />
          </div>

          {/* 🔹 Champ Mot de Passe (bien en dessous du champ Email) */}
          <div className="mb-6">
            <label className="block text-lg font-semibold mb-2">Mot de passe</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-4 rounded-lg bg-white text-black text-lg"
              required
            />
          </div>

          {/* 🔹 Checkbox "Se souvenir de moi" */}
          <div className="flex items-center mb-6">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
              className="mr-2"
            />
            <label className="text-lg">Se souvenir de moi</label>
          </div>

          {/* 🔹 Bouton Connexion (plus grand et espacé) */}
          <button type="submit" className="bg-green-700 hover:bg-green-800 p-4 text-lg rounded-lg text-white font-bold mb-6">
            🔓 ENTRER
          </button>
        </form>

        {/* 🔹 Lien "Mot de passe oublié" */}
        <div className="text-center mb-4">
          <Link to="/forgot-password" className="text-white text-lg hover:underline">
            🔑 Mot de passe oublié ?
          </Link>
        </div>

        {/* 🔹 Lien "S'inscrire" */}
        <div className="text-center">
          <span className="text-gray-300 text-lg">Pas encore inscrit ? </span>
          <Link to="/register" className="text-blue-400 text-lg font-bold hover:underline">
            ✍️ S'inscrire
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
