import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";
import Navbar from "../components/Navbar"; // Barre supÃ©rieure
import UserBar from "../components/UserBar"; // Barre utilisateur

const WalletPage = () => {
  const [cards, setCards] = useState([]);
  const [newCardNumber, setNewCardNumber] = useState("");
  const [expiryMonth, setExpiryMonth] = useState("");
  const [expiryYear, setExpiryYear] = useState("");
  const [cvc, setCvc] = useState("");
  const [cardHolder, setCardHolder] = useState("");
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
    } else {
      setUser(storedUser);
      fetchCards(storedUser.token);
    }
  }, [navigate]);

  // ðŸ”¹ RÃ©cupÃ©rer les cartes de l'utilisateur
  const fetchCards = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/cards", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setCards(data);
    } catch (error) {
      console.error("Erreur rÃ©cupÃ©ration cartes", error);
    }
  };

  // ðŸ”¹ Ajouter une carte
  const addCard = async () => {
    if (cards.length >= 2) {
      alert("Vous avez dÃ©jÃ  2 cartes, supprimez-en une avant d'ajouter une nouvelle.");
      return;
    }

    if (newCardNumber.length === 16 && expiryMonth.length === 2 && expiryYear.length === 2 && (cvc.length === 3 || cvc.length === 4) && cardHolder.trim() !== "") {
      try {
        const response = await fetch("http://localhost:5000/api/cards", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify({
            number: newCardNumber,
            expiryMonth,
            expiryYear,
            cvc,
            holder: cardHolder,
          }),
        });

        const newCard = await response.json();
        setCards([...cards, newCard]);

        // RÃ©initialiser le formulaire
        setNewCardNumber("");
        setExpiryMonth("");
        setExpiryYear("");
        setCvc("");
        setCardHolder("");
      } catch (error) {
        console.error("Erreur ajout carte", error);
      }
    } else {
      alert("Veuillez remplir correctement tous les champs.");
    }
  };

  // ðŸ”¹ Supprimer une carte
  const deleteCard = async (cardId) => {
    try {
      await fetch(`http://localhost:5000/api/cards/${cardId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${user.token}` },
      });
      setCards(cards.filter((card) => card._id !== cardId));
    } catch (error) {
      console.error("Erreur suppression carte", error);
    }
  };

  return (
    <div style={{ backgroundColor: "#f5f5f5", minHeight: "100vh" }}>
      {/* ðŸ”¹ Barre de navigation */}
      <Navbar />

      {/* ðŸ”¹ Barre utilisateur */}
      <UserBar />

      <div style={{ maxWidth: "600px", margin: "auto", padding: "20px", backgroundColor: "#fff", borderRadius: "10px", boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)", marginTop: "20px" }}>
        <h2 style={{ textAlign: "center", color: "#333" }}>
          <Icon icon="mdi:credit-card-outline" width="30" /> Mon Portefeuille
        </h2>
        <h3 style={{ textAlign: "center", color: "#666" }}>Cartes bancaires</h3>

        {cards.map((card) => (
          <div key={card._id} style={{ border: "1px solid #ddd", borderRadius: "8px", padding: "10px", marginBottom: "10px", display: "flex", alignItems: "center", justifyContent: "space-between" }}>
            <div>
              <p>
                <Icon icon="mdi:credit-card-chip" width="20" /> **** **** **** {card.number.slice(-4)}
              </p>
              <p>Expiration: {card.expiryMonth}/{card.expiryYear}</p>
              <p>Titulaire: {card.holder}</p>
            </div>
            <button onClick={() => deleteCard(card._id)} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
              <Icon icon="mdi:trash-can-outline" width="20" />
            </button>
          </div>
        ))}

        {cards.length < 2 && (
          <div style={{ display: "flex", flexDirection: "column", gap: "10px", marginTop: "20px" }}>
            <h3 style={{ textAlign: "center" }}>Ajouter une carte</h3>
            <input type="text" value={newCardNumber} onChange={(e) => setNewCardNumber(e.target.value.replace(/\D/g, "").slice(0, 16))} placeholder="NumÃ©ro de carte (16 chiffres)" maxLength="16" />
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <input type="text" value={expiryMonth} onChange={(e) => setExpiryMonth(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="MM" maxLength="2" style={{ width: "48%" }} />
              <input type="text" value={expiryYear} onChange={(e) => setExpiryYear(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="YY" maxLength="2" style={{ width: "48%" }} />
            </div>
            <input type="text" value={cvc} onChange={(e) => setCvc(e.target.value.replace(/\D/g, "").slice(0, 4))} placeholder="CVC (3-4 chiffres)" maxLength="4" />
            <input type="text" value={cardHolder} onChange={(e) => setCardHolder(e.target.value)} placeholder="Nom et prÃ©nom du propriÃ©taire" />

            <button onClick={addCard} style={{ backgroundColor: "#4CAF50", color: "white", padding: "10px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
              <Icon icon="mdi:plus-circle" width="20" /> Ajouter
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default WalletPage;
