import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userData = {
        email: email.trim(),
        password: password.trim()
    };

    try {
        const response = await axios.post("http://localhost:5000/api/users/login", userData, {
            headers: { "Content-Type": "application/json" }
        });

        console.log("✅ Connexion réussie :", response.data);

        localStorage.setItem("token", response.data.token); // Stocke le token

    } catch (error) {
        console.error("❌ Erreur :", error.response?.data?.message || "Erreur inconnue");
    }
};


  return (
    <div>
      {/* Liens en haut à droite */}
      <div className="absolute top-4 right-4 flex gap-4">
        <Link to="/" className="text-white text-lg font-bold hover:underline">
          Accueil
        </Link>
        <Link to="/products" className="text-white text-lg font-bold hover:underline">
          Produits
        </Link>
      </div>

      {/* Page de connexion */}
      <div style={{
        backgroundImage: `url('/assets/marvelstore.jpg')`, // ✅ Chemin corrigé
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
        <h2>Se connecter</h2>
        {error && <p style={{ color: "red" }}>{error}</p>}

        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column" }}>
          <input 
            type="email" 
            placeholder="Email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            className="p-3 mb-4 rounded bg-white text-black"
            required 
          />
          
          <input 
            type="password" 
            placeholder="Mot de passe" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            className="p-3 mb-4 rounded bg-white text-black"
            required 
          />
          
          <button type="submit">Se connecter</button>
        </form>

        {/* 🔹 Lien vers "Mot de passe oublié" */}
        <div className="text-center mt-4">
          <Link to="/forgot-password" className="text-blue-500 hover:underline">
            🔑 Mot de passe oublié ?
          </Link>
        </div>

        <p>Pas encore de compte ? <Link to="/register">Créer un compte</Link></p>
      </div>
    </div>
  );
};

export default LoginPage;
