import React, { useEffect, useState } from "react";
import axios from "axios";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);
  
  function searchProducts() {
    const query = document.getElementById("search").value.toLowerCase();
    const filteredProducts = products.filter(product => product.name.toLowerCase().includes(query));
    const productList = document.getElementById("product-list");
    productList.innerHTML = "";
    filteredProducts.forEach(product => {
        productList.innerHTML += `
            <div class="product">
                <img src="${product.image}" alt="${product.name}">
                <h3>${product.name}</h3>
                <p>Prix: ${product.price} €</p>
                <button onclick="addToCart(${product.id})">Ajouter au panier</button>
            </div>
        `;
    });
}



  return (
    <div style={{
        //backgroundColor: "red",
        backgroundImage: `url('/assets/marvelstoreproduits.jpeg')`, // ✅ Vérifie bien ce chemin
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2>Nos Produits</h2>
      <input type="text" id="search" placeholder="Rechercher un produit..." onkeyup="searchProducts()"></input>
      <div id="product-list"></div>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {products.map((product) => (
          <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px" }}>
            <h3>{product.name}</h3>
            <p>Prix : {product.price}€</p>
            {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
