import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react"; 

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const [address, setAddress] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cardDetails, setCardDetails] = useState("");
  const [paypalEmail, setPaypalEmail] = useState("");
  const navigate = useNavigate();
  const location = useLocation(); // ‚úÖ Utilisation correcte de location

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = () => {
    alert(`Paiement effectu√© avec succ√®s ! Votre commande arrivera le ${getDeliveryDate()}`);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      {/* Barre de navigation */}
      <header className="fixed top-0 left-0 w-full bg-gray-900 text-white py-3 shadow-lg z-10">
        <nav className="flex justify-center gap-6">
          {[
            { to: "/", icon: "mdi:home", label: "Accueil" },
            { to: "/products", icon: "mdi:shopping", label: "Produits" },
            { to: "/cart", icon: "mdi:cart", label: "Panier" },
            { to: "/account", icon: "mdi:account", label: "Compte" },
            { to: "/wallet", icon: "mdi:credit-card", label: "Portefeuille" },
            { to: "/addresses", icon: "mdi:map-marker", label: "Adresses" },
          ].map((item, index) => (
            <Link key={index} to={item.to} className="flex items-center">
              <Icon icon={item.icon} width="32" color={location.pathname === item.to ? "red" : "white"} />
            </Link>
          ))}
        </nav>
      </header>

      {/* Contenu principal */}
      <main className="mt-20 px-4 flex flex-col items-center">
        <h2 className="text-3xl font-bold mb-6">üõí Mon Panier</h2>

        {cart.length === 0 ? (
          <p className="text-xl">Votre panier est vide.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 w-full max-w-6xl">
            {cart.map((product, index) => (
              <div key={index} className="border p-4 flex flex-col items-center bg-white shadow-lg rounded">
                <img src={product.image} alt={product.name} className="w-40 h-40 object-cover mb-2" />
                <h3 className="text-lg font-bold">{product.name}</h3>
                <p>Prix : {product.price}‚Ç¨</p>
                <button
                  onClick={() => removeFromCart(index)}
                  className="bg-red-500 text-white px-3 py-1 rounded mt-2"
                >
                  Supprimer
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Section de paiement */}
        {cart.length > 0 && (
          <div className="mt-6 p-4 bg-white shadow-lg rounded w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">üí≥ Paiement</h3>

            <label className="block mb-2">Adresse de livraison:</label>
            <input
              type="text"
              className="w-full p-2 border rounded mb-4"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Votre adresse compl√®te"
            />

            <label className="block mb-2">Mode de paiement:</label>
            <select
              className="w-full p-2 border rounded mb-4"
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
            >
              <option value="card">Carte bancaire</option>
              <option value="paypal">PayPal</option>
            </select>

            {paymentMethod === "card" && (
              <input
                type="text"
                className="w-full p-2 border rounded mb-4"
                value={cardDetails}
                onChange={(e) => setCardDetails(e.target.value)}
                placeholder="Num√©ro de carte"
              />
            )}

            {paymentMethod === "paypal" && (
              <input
                type="email"
                className="w-full p-2 border rounded mb-4"
                value={paypalEmail}
                onChange={(e) => setPaypalEmail(e.target.value)}
                placeholder="Email PayPal"
              />
            )}

            <p className="mb-4">üìÖ Date de livraison pr√©vue: <strong>{getDeliveryDate()}</strong></p>

            <button
              onClick={handlePayment}
              className="w-full bg-green-500 text-white p-3 rounded"
            >
              Effectuer le paiement
            </button>
          </div>
        )}
      </main>

      {/* Footer utilisateur */}
      <footer className="fixed bottom-0 left-0 w-full bg-gray-900 text-white py-2 px-4 flex justify-between items-center">
        <h1 className="text-lg">üõçÔ∏è La meilleure Boutique</h1>
        <div className="flex items-center">
          <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
          {user ? (
            <>
              <span className="ml-2">{user.name}</span>
              <button
                onClick={handleLogout}
                className="ml-4 bg-red-500 text-white px-3 py-1 rounded"
              >
                D√©connexion
              </button>
            </>
          ) : (
            <button
              onClick={() => navigate("/login")}
              className="ml-4 bg-blue-500 text-white px-3 py-1 rounded"
            >
              Login
            </button>
          )}
        </div>
      </footer>
    </div>
  );
};

export default CartPage;
