import React, { useState, useEffect } from "react"; 
import { useNavigate, Link, useLocation } from "react-router-dom"; 
import { Icon } from "@iconify/react";

const WalletPage = () => {
  const [cards, setCards] = useState([]); 
  const [newCardNumber, setNewCardNumber] = useState(""); 
  const [expiryMonth, setExpiryMonth] = useState(""); 
  const [expiryYear, setExpiryYear] = useState(""); 
  const [cvc, setCvc] = useState(""); 
  const [cardHolder, setCardHolder] = useState(""); 
  const [user, setUser] = useState(null); 
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate(); 
  const location = useLocation();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
    } else {
      setUser(storedUser);
      fetchCards(storedUser.token, storedUser._id);
    }
  }, [navigate]);

  const fetchCards = async (token, userId) => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/api/cards/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Vérifier le type de réponse (JSON)
      const contentType = response.headers.get("content-type");
      let data;
      
      if (contentType && contentType.includes("application/json")) {
        data = await response.json();
      } else {
        const text = await response.text();
        console.error("🚨 Réponse non JSON reçue:", text);
        return;
      }

      console.log("📥 Cartes récupérées :", data);

      // ✅ Filtrer les cartes pour ne garder que celles de l'utilisateur connecté
      const userCards = Array.isArray(data) ? data.filter(card => card.userId === userId) : [];
      setCards(userCards);
    } catch (error) {
      console.error("❌ Erreur lors de la récupération des cartes:", error);
      setCards([]);
    } finally {
      setLoading(false);
    }
  };

  const addCard = async () => {
    if (!user || !user._id) {
      console.error("❌ Erreur : utilisateur non trouvé");
      alert("Utilisateur non trouvé, veuillez vous reconnecter.");
      return;
    }

    const newCard = {
      userId: user._id,
      holder: cardHolder.trim(),
      number: newCardNumber.trim(),
      expiryMonth: expiryMonth.trim(),
      expiryYear: expiryYear.trim(),
      cvc: cvc.trim(),
    };

    console.log("📤 Envoi de la carte :", newCard);

    try {
      const response = await fetch("http://localhost:5000/api/cards", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`, // ✅ Correction de l'Authorization
        },
        body: JSON.stringify(newCard),
      });

      const data = await response.json();
      console.log("📩 Réponse serveur :", data);

      if (!response.ok) throw new Error(data.message);

      alert("Carte ajoutée avec succès !");
      fetchCards(user.token, user._id); // ✅ Rafraîchir la liste des cartes après ajout
    } catch (error) {
      console.error("❌ Erreur lors de l'ajout :", error.message);
    }
  };

  const deleteCard = async (cardId) => {
    console.log(`🗑️ Suppression de la carte ID: ${cardId}`);
  
    try {
      const response = await fetch(`http://localhost:5000/api/cards/${cardId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${user.token}` },
      });

      const contentType = response.headers.get("content-type");
      let data;
      
      if (contentType && contentType.includes("application/json")) {
        data = await response.json();
      } else {
        data = await response.text();
        console.error("🚨 Réponse serveur non JSON:", data);
      }
  
      console.log("📩 Réponse serveur :", data);
  
      if (!response.ok) throw new Error(data.error || "Erreur lors de la suppression");
  
      setCards(cards.filter((card) => card._id !== cardId));
      alert("Carte supprimée avec succès !");
    } catch (error) {
      console.error("❌ Erreur lors de la suppression :", error.message);
      alert(`Erreur : ${error.message}`);
    }
  };  

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div style={{ backgroundColor: "#f5f5f5", minHeight: "100vh", paddingTop: "80px" }}>
      <header
        style={{
          display: "flex",
          flexDirection: "column",
          backgroundColor: "#222",
          color: "white",
          padding: "10px 1px",
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          zIndex: 10,
        }}
      >
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          {[{ path: "/", icon: "mdi:home" }, { path: "/products", icon: "mdi:shopping" }, { path: "/cart", icon: "mdi:cart" }, { path: "/orders", icon: "mdi:package-variant-closed" }, { path: "/account", icon: "mdi:account" }, { path: "/wallet", icon: "mdi:credit-card" }, { path: "/addresses", icon: "mdi:map-marker" }].map((item) => (
            <Link key={item.path} to={item.path} className="nav-link">
              <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
            </Link>
          ))}
        </nav>

        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>🛍️ Passer à la caisse</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
            {user ? (
              <>
                <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>
                  Déconnexion
                </button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>
                Login
              </button>
            )}
          </div>
        </div>
      </header>

      <div style={{ maxWidth: "600px", margin: "auto", padding: "20px", backgroundColor: "#fff", borderRadius: "10px", boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)", marginTop: "40px" }}>
        <h2 style={{ textAlign: "center", color: "#333" }}>💳 Mon Portefeuille</h2>

        {!loading && cards.length < 2 && (
          <div style={{ display: "flex", flexDirection: "column", gap: "10px", marginTop: "20px" }}>
            <h3 style={{ textAlign: "center" }}>Ajouter une carte</h3>
            <input
              type="text"
              value={newCardNumber}
              onChange={(e) => setNewCardNumber(e.target.value.replace(/\D/g, "").slice(0, 16))}
              placeholder="Numéro de carte (16 chiffres)"
              maxLength="16"
            />
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <input
                type="text"
                value={expiryMonth}
                onChange={(e) => setExpiryMonth(e.target.value.replace(/\D/g, "").slice(0, 2))}
                placeholder="MM"
                maxLength="2"
                style={{ width: "48%" }}
              />
              <input
                type="text"
                value={expiryYear}
                onChange={(e) => setExpiryYear(e.target.value.replace(/\D/g, "").slice(0, 2))}
                placeholder="YY"
                maxLength="2"
                style={{ width: "48%" }}
              />
            </div>
            <input
              type="text"
              value={cvc}
              onChange={(e) => setCvc(e.target.value.replace(/\D/g, "").slice(0, 4))}
              placeholder="CVC (3-4 chiffres)"
              maxLength="4"
            />
            <input
              type="text"
              value={cardHolder}
              onChange={(e) => setCardHolder(e.target.value)}
              placeholder="Nom et prénom du propriétaire"
            />
            <button onClick={addCard} style={{ backgroundColor: "#4CAF50", color: "white", padding: "10px", borderRadius: "5px" }}>
              Ajouter
            </button>
          </div>
        )}

        {loading ? <p>Chargement...</p> : cards.map((card) => (
          <div key={card._id} style={{ border: "50px solid #ddd", padding: "10px", marginBottom: "50px" }}>
            <p>**** **** **** {card.number?.slice(-4)}</p>
            <p>Expiration: {card.expiryMonth}/{card.expiryYear} - {card.holder}</p>
            <button onClick={() => deleteCard(card._id)}>Supprimer</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WalletPage;
