import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [menuOpen, setMenuOpen] = useState(false);
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [formData, setFormData] = useState({ size: "", color: "", quantity: 1 });
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const openProductModal = (product) => {
    setSelectedProduct(product);
  };

  const closeProductModal = () => {
    setSelectedProduct(null);
  };

  const handleAddToCart = () => {
    if (!selectedProduct) return;

    const updatedCart = [...cart, {
      _id: selectedProduct._id,
      name: selectedProduct.name,
      price: selectedProduct.price,
      image: selectedProduct.image,
      ...formData,
    }];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    closeProductModal();
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          <Link to="/" className="nav-link"><Icon icon="mdi:home" width="32" /></Link>
          <Link to="/products" className="nav-link"><Icon icon="mdi:shopping" width="32" /></Link>
          <Link to="/cart" className="nav-link"><Icon icon="mdi:cart" width="32" /></Link>
          <Link to="/account" className="nav-link"><Icon icon="mdi:account" width="32" /></Link>
        </nav>
      </header>

      <div style={{ marginTop: "120px", flex: 1, padding: "20px" }}>
        <h2>üõçÔ∏è Nos Produits</h2>
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
        />

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
          {filteredProducts.map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}‚Ç¨</p>
              <p>{product.description}</p>
              {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
              <button onClick={() => openProductModal(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
                Ajouter au panier
              </button>
            </div>
          ))}
        </div>
      </div>

      {selectedProduct && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100%", height: "100%", backgroundColor: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center" }}>
          <div style={{ backgroundColor: "white", padding: "20px", borderRadius: "10px", width: "400px" }}>
            <h2>{selectedProduct.name}</h2>
            <p>{selectedProduct.description}</p>
            {(selectedProduct.category === "chaussures" || selectedProduct.category === "t-shirts") && (
              <div>
                <label>Taille:
                  <input type="text" name="size" value={formData.size} onChange={handleChange} />
                </label>
              </div>
            )}
            <div>
              <label>Couleur:
                <input type="text" name="color" value={formData.color} onChange={handleChange} />
              </label>
            </div>
            <div>
              <label>Quantit√©:
                <input type="number" name="quantity" min="1" value={formData.quantity} onChange={handleChange} />
              </label>
            </div>
            <button onClick={handleAddToCart} className="bg-green-500 text-white px-4 py-2 rounded mt-2">Confirmer</button>
            <button onClick={closeProductModal} className="bg-red-500 text-white px-4 py-2 rounded mt-2">Annuler</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
