import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const CartPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // R√©cup√©ration de l'utilisateur connect√©
  const [user, setUser] = useState(() => JSON.parse(localStorage.getItem("user")) || null);

  // Chargement du panier de l'utilisateur
  const [cart, setCart] = useState(() => {
    if (user) {
      return JSON.parse(localStorage.getItem(`cart_${user.id}`)) || [];
    }
    return [];
  });

  // √âtats pour l'adresse et le paiement
  const [address, setAddress] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  
  // √âtats pour les cartes
  const [savedCards, setSavedCards] = useState([]);
  const [cardDetails, setCardDetails] = useState({ number: "", date: "", cvc: "" });
  const [cardHolder, setCardHolder] = useState("");
  const [saveNewCard, setSaveNewCard] = useState(false);
  
  // Email PayPal
  const [paypalEmail, setPaypalEmail] = useState("");

  useEffect(() => {
    if (user) {
      localStorage.setItem(`cart_${user.id}`, JSON.stringify(cart));
    }
  }, [cart, user]);

  useEffect(() => {
    if (user) {
      axios.get(`http://localhost:5000/api/cards/user/${user.id}`)
        .then((res) => setSavedCards(res.data))
        .catch((err) => console.error("Erreur r√©cup√©ration cartes :", err));
    }
  }, [user]);

  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = async () => {
    try {
      await axios.post("http://localhost:5000/api/updateStock", { cart });

      if (paymentMethod === "card" && saveNewCard) {
        await axios.post("http://localhost:5000/api/cards", {
          userId: user.id,
          holder: cardHolder,
          number: cardDetails.number,
          expiryMonth: cardDetails.date.split("/")[0],
          expiryYear: cardDetails.date.split("/")[1],
          cvc: cardDetails.cvc
        });
      }

      alert(`Paiement effectu√© avec succ√®s ! Votre commande arrivera le ${getDeliveryDate()}`);
      setCart([]);
      localStorage.removeItem(`cart_${user.id}`);

    } catch (error) {
      console.error("Erreur lors du paiement :", error);
      alert("Une erreur est survenue lors du paiement. Veuillez r√©essayer.");
    }
  };

  return (
    <div>
      {/* Barre sup√©rieure */}
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          {[
            { path: "/", icon: "mdi:home" },
            { path: "/products", icon: "mdi:shopping" },
            { path: "/cart", icon: "mdi:cart" },
            { path: "/account", icon: "mdi:account" },
            { path: "/wallet", icon: "mdi:credit-card" },
            { path: "/addresses", icon: "mdi:map-marker" },
          ].map((item) => (
            <Link key={item.path} to={item.path}>
              <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
            </Link>
          ))}
        </nav>

        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>üõçÔ∏è Passer √† la caisse</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" />
            {user ? (
              <>
                <span style={{ marginLeft: "10px" }}>{user.name}</span>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>D√©connexion</button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
            )}
          </div>
        </div>
      </header>

      <div style={{ backgroundImage: `url('/assets/marvelstorepanier.jpeg')`, backgroundSize: "cover", marginTop: "120px", flex: 1, padding: "20px" }}>
        <main>
          <h2>üõí Mon Panier</h2>
          <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
            {cart.map((product, index) => (
              <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#222", color: "#fff" }}>
                <h3>{product.name}</h3>
                <p>Prix : {product.price}‚Ç¨</p>
                {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
                <button onClick={() => removeFromCart(index)} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px", marginTop: "10px" }}>Supprimer</button>
              </div>
            ))}
          </div>

          {cart.length > 0 && (
            <div style={{ backgroundColor: "#222", padding: "20px", borderRadius: "10px", marginTop: "20px", color: "white" }}>
              <h3>üí≥ Paiement</h3>
              <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Adresse de livraison" />

              <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
                <option value="card">Carte bancaire</option>
                <option value="paypal">PayPal</option>
              </select>

              {paymentMethod === "card" && (
                <>
                  <h4>Cartes enregistr√©es :</h4>
                  {savedCards.map((card) => (
                    <div key={card._id}>
                      <input type="radio" name="selectedCard" onChange={() => setCardDetails({ number: card.number, date: `${card.expiryMonth}/${card.expiryYear}`, cvc: "‚Ä¢‚Ä¢‚Ä¢" })} />
                      <span>**** {card.number.slice(-4)} (Expire: {card.expiryMonth}/{card.expiryYear})</span>
                    </div>
                  ))}
                  <input type="checkbox" checked={saveNewCard} onChange={() => setSaveNewCard(!saveNewCard)} />
                  <label>Enregistrer cette carte</label>
                </>
              )}

              <button onClick={handlePayment} style={{ backgroundColor: "green", color: "white", padding: "10px", borderRadius: "5px", width: "100%" }}>Payer</button>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default CartPage;
