import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import TopBar from "../components/TopBar";  // Barre supÃ©rieure
import UserBar from "../components/UserBar"; // Barre utilisateur

const WalletPage = () => {
  const [cards, setCards] = useState([]);
  const [newCardNumber, setNewCardNumber] = useState("");
  const [expiryMonth, setExpiryMonth] = useState("");
  const [expiryYear, setExpiryYear] = useState("");
  const [cvc, setCvc] = useState("");
  const [cardHolder, setCardHolder] = useState("");
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
    } else {
      setUser(storedUser);
      fetchCards(storedUser.token);
    }
  }, [navigate]);

  // ðŸ”¹ RÃ©cupÃ©rer les cartes de l'utilisateur
  const fetchCards = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/cards", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setCards(data);
    } catch (error) {
      console.error("Erreur rÃ©cupÃ©ration cartes", error);
    }
  };

  // ðŸ”¹ Ajouter une carte
  const addCard = async () => {
    if (cards.length >= 2) {
      alert("Vous avez dÃ©jÃ  2 cartes, supprimez-en une avant d'ajouter une nouvelle.");
      return;
    }

    if (newCardNumber.length === 16 && expiryMonth.length === 2 && expiryYear.length === 2 && (cvc.length === 3 || cvc.length === 4) && cardHolder.trim() !== "") {
      try {
        const response = await fetch("http://localhost:5000/api/cards", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify({
            number: newCardNumber,
            expiryMonth,
            expiryYear,
            cvc,
            holder: cardHolder,
          }),
        });

        const newCard = await response.json();
        setCards([...cards, newCard]);

        // RÃ©initialiser le formulaire
        setNewCardNumber("");
        setExpiryMonth("");
        setExpiryYear("");
        setCvc("");
        setCardHolder("");
      } catch (error) {
        console.error("Erreur ajout carte", error);
      }
    } else {
      alert("Veuillez remplir correctement tous les champs.");
    }
  };

  // ðŸ”¹ Supprimer une carte
  const deleteCard = async (cardId) => {
    try {
      await fetch(`http://localhost:5000/api/cards/${cardId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${user.token}` },
      });
      setCards(cards.filter((card) => card._id !== cardId));
    } catch (error) {
      console.error("Erreur suppression carte", error);
    }
  };

  return (
    <div style={{ minHeight: "100vh", backgroundColor: "#1e1e1e", color: "#fff" }}>
      {/* ðŸ”¹ Barre supÃ©rieure */}
      <TopBar />
      
      <div style={{ display: "flex" }}>
        {/* ðŸ”¹ Barre utilisateur */}
        <UserBar />

        <div style={{ flex: 1, padding: "20px", textAlign: "center" }}>
          <h2>ðŸ’³ Mon Portefeuille</h2>
          <h3>Cartes bancaires</h3>

          {cards.map((card) => (
            <div key={card._id} style={{ border: "1px solid white", padding: "10px", marginBottom: "10px" }}>
              <p>**** **** **** {card.number.slice(-4)}</p>
              <p>Exp: {card.expiryMonth}/{card.expiryYear} - {card.holder}</p>
              <button onClick={() => deleteCard(card._id)} style={{ backgroundColor: "red", color: "white", padding: "5px" }}>
                Supprimer
              </button>
            </div>
          ))}

          {cards.length < 2 && (
            <div style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "400px", margin: "auto" }}>
              <input type="text" value={newCardNumber} onChange={(e) => setNewCardNumber(e.target.value.replace(/\D/g, "").slice(0, 16))} placeholder="NumÃ©ro de carte (16 chiffres)" maxLength="16" />
              <input type="text" value={expiryMonth} onChange={(e) => setExpiryMonth(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="MM" maxLength="2" />
              <input type="text" value={expiryYear} onChange={(e) => setExpiryYear(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="YY" maxLength="2" />
              <input type="text" value={cvc} onChange={(e) => setCvc(e.target.value.replace(/\D/g, "").slice(0, 4))} placeholder="CVC (3-4 chiffres)" maxLength="4" />
              <input type="text" value={cardHolder} onChange={(e) => setCardHolder(e.target.value)} placeholder="Nom et prÃ©nom du propriÃ©taire" />
              <button onClick={addCard} style={{ backgroundColor: "#4CAF50", color: "white", padding: "10px" }}>Ajouter</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WalletPage;
