import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react"; // Assurez-vous d'avoir install√© @iconify/react

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Charger l'utilisateur depuis localStorage au montage
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
        {/* üîπ Barre sup√©rieure + Navigation */}
      <header className="fixed top-0 left-0 w-full bg-gray-900 text-white p-4 z-10">
        <nav className="flex justify-center gap-5">
          <Link to="/">
            <Icon icon="mdi:home" width="32" color="white" />
          </Link>
          <Link to="/products">
            <Icon icon="mdi:shopping" width="32" color="white" />
          </Link>
          <Link to="/cart">
            <Icon icon="mdi:cart" width="32" color="red" />
          </Link>
          <Link to="/account">
            <Icon icon="mdi:account" width="32" color="white" />
          </Link>
          <Link to="/wallet">
            <Icon icon="mdi:credit-card" width="32" color="white" />
          </Link>
          <Link to="/addresses">
            <Icon icon="mdi:map-marker" width="32" color="white" />
          </Link>
        </nav>
        <div className="flex justify-between items-center mt-3 px-5">
          <h1 className="text-lg">üõçÔ∏è La meilleure Boutique</h1>
          <div className="flex items-center">
            <Icon icon="mdi:account-circle" width="40" className="cursor-pointer" />
            {user ? (
              <>
                <span className="ml-3 text-md">{user.name}</span>
                <button onClick={handleLogout} className="ml-3 bg-red-500 text-white px-3 py-1 rounded">
                  D√©connexion
                </button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} className="ml-3 bg-blue-500 text-white px-3 py-1 rounded">
                Login
              </button>
            )}
          </div>
        </div>
      </header>
      <h2 className="text-3xl font-bold mb-6">üõí Mon Panier</h2>

      {cart.length === 0 ? (
        <p className="text-xl">Votre panier est vide.</p>
      ) : (
        <div className="w-full max-w-lg">
          {cart.map((product, index) => (
            <div key={index} className="border p-4 flex justify-between items-center bg-white mb-4 shadow-lg rounded">
              <div>
                <h3 className="text-lg font-bold">{product.name}</h3>
                <p>Prix : {product.price}‚Ç¨</p>
                {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
              </div>
              <button
                onClick={() => removeFromCart(index)}
                className="bg-red-500 text-white px-3 py-1 rounded"
              >
                Supprimer
              </button>
            </div>
          ))}
        </div>
      )}

      {/* üîπ Barre sup√©rieure + Navigation */}
      <header className="fixed top-0 left-0 w-full bg-gray-900 text-white p-4 z-10">
        <nav className="flex justify-center gap-5">
          <Link to="/">
            <Icon icon="mdi:home" width="32" color="white" />
          </Link>
          <Link to="/products">
            <Icon icon="mdi:shopping" width="32" color="white" />
          </Link>
          <Link to="/cart">
            <Icon icon="mdi:cart" width="32" color="red" />
          </Link>
          <Link to="/account">
            <Icon icon="mdi:account" width="32" color="white" />
          </Link>
          <Link to="/wallet">
            <Icon icon="mdi:credit-card" width="32" color="white" />
          </Link>
          <Link to="/addresses">
            <Icon icon="mdi:map-marker" width="32" color="white" />
          </Link>
        </nav>
        <div className="flex justify-between items-center mt-3 px-5">
          <h1 className="text-lg">üõçÔ∏è La meilleure Boutique</h1>
          <div className="flex items-center">
            <Icon icon="mdi:account-circle" width="40" className="cursor-pointer" />
            {user ? (
              <>
                <span className="ml-3 text-md">{user.name}</span>
                <button onClick={handleLogout} className="ml-3 bg-red-500 text-white px-3 py-1 rounded">
                  D√©connexion
                </button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} className="ml-3 bg-blue-500 text-white px-3 py-1 rounded">
                Login
              </button>
            )}
          </div>
        </div>
      </header>
    </div>
  );
};

export default CartPage;
