import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError("Veuillez remplir tous les champs !");
      return;
    }

    console.log("üü¢ Donn√©es envoy√©es au backend :", { email, password });

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });
      alert("Connexion r√©ussie !");
      console.log("üü¢ R√©ponse du serveur :", response.data);

      // ‚úÖ Redirection vers la page des produits apr√®s connexion
      navigate("/products");
    } catch (error) {
      console.error("‚ùå Erreur :", error.response?.data?.message || "Probl√®me serveur");
      setError(error.response?.data?.message || "Erreur lors de la connexion.");
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px", textAlign: "center" }}>
      <h2>Se connecter</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div style={{
        //backgroundColor: "red",
        backgroundImage: `url('/assets/marvelstore.webp')`, // ‚úÖ V√©rifie bien ce chemin
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}></div>

      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column" }}>
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
        
        <input 
          type="password" 
          placeholder="Mot de passe" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
        
        <button type="submit">Se connecter</button>
      </form>

      <p>Pas encore de compte ? <a href="/register">Cr√©er un compte</a></p>
    </div>
  );
};

export default LoginPage;
