import React, { useEffect, useState } from "react";
import axios from "axios";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => {
        console.log("Produits reçus :", response.data);
        setProducts(response.data);
      })
      .catch((error) => console.error("Erreur :", error));
  }, []);

  return (
    <div style={{
        //backgroundColor: "red",
        backgroundImage: `url('/assets/panier.jpg')`, // ✅ Vérifie bien ce chemin
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2>Nos Produits</h2>
      {products.length === 0 ? (
        <p>Aucun produit trouvé.</p>
      ) : (
        <div style={{ display: "flex", flexWrap: "wrap" }}>
          {products.map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px" }}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}€</p>
              <img src={product.image} alt={product.name} style={{ width: "150px" }} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
