import React, { useState, useEffect } from "react";
import axios from "axios";

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/orders");
        setOrders(response.data);
      } catch (err) {
        console.error("Erreur lors de la r√©cup√©ration des commandes:", err);
        setError("Impossible de charger les commandes.");
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  const reorder = async (orderId) => {
    try {
      await axios.post("http://localhost:5000/api/orders/reorder", { orderId });
      alert("Commande repass√©e avec succ√®s !");
    } catch (err) {
      console.error("Erreur lors du reorder:", err);
      alert("Erreur lors de la commande.");
    }
  };

  return (
    <div>
      <h2>üì¶ Mes Commandes</h2>
      {loading && <p>Chargement des commandes...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {orders.map((order) => (
        <div key={order.id}>
          <p>Commande #{order.id} - Statut: {order.status}</p>
          {order.status === "Livr√©e" && (
            <button onClick={() => reorder(order.id)}>Acheter √† nouveau</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default OrdersPage;
