import React, { useState, useEffect } from "react";
import axios from "axios";

const AddressesPage = () => {
  const [addresses, setAddresses] = useState([]);
  const [newAddress, setNewAddress] = useState("");

  useEffect(() => {
    const fetchAddresses = async () => {
      const response = await axios.get("http://localhost:5000/api/addresses");
      setAddresses(response.data);
    };
    fetchAddresses();
  }, []);

  const addAddress = async () => {
    await axios.post("http://localhost:5000/api/addresses", { address: newAddress });
    setAddresses([...addresses, newAddress]);
    setNewAddress("");
  };

  return (
    <div>
      <h2>üè† Mes Adresses</h2>
      {addresses.map((address, index) => (
        <p key={index}>{address}</p>
      ))}
      <input type="text" value={newAddress} onChange={(e) => setNewAddress(e.target.value)} placeholder="Ajouter une adresse" />
      <button onClick={addAddress}>Ajouter</button>
    </div>
  );
};

export default AddressesPage;
