import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const WalletPage = () => {
  const [cards, setCards] = useState([]);
  const [newCardNumber, setNewCardNumber] = useState("");
  const [expiryMonth, setExpiryMonth] = useState("");
  const [expiryYear, setExpiryYear] = useState("");
  const [cvc, setCvc] = useState("");
  const [cardHolder, setCardHolder] = useState("");
  const navigate = useNavigate();

  const detectCardType = (number) => {
    const visaRegex = /^4[0-9]{12}(?:[0-9]{3})?$/;
    const masterCardRegex = /^5[1-5][0-9]{14}$/;
    if (visaRegex.test(number)) return "Visa";
    if (masterCardRegex.test(number)) return "Mastercard";
    return "Inconnu";
  };

  const addCard = () => {
    if (
      newCardNumber.length === 16 &&
      expiryMonth.length === 2 &&
      expiryYear.length === 2 &&
      (cvc.length === 3 || cvc.length === 4) &&
      cardHolder.trim() !== ""
    ) {
      const cardType = detectCardType(newCardNumber);
      const newCard = {
        number: newCardNumber,
        type: cardType,
        expiry: `${expiryMonth}/${expiryYear}`,
        cvc,
        holder: cardHolder,
      };

      setCards([...cards, newCard]);
      setNewCardNumber("");
      setExpiryMonth("");
      setExpiryYear("");
      setCvc("");
      setCardHolder("");
    } else {
      alert("Veuillez remplir correctement tous les champs.");
    }
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/marvelstorewallet.jpeg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        //alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
    <div style={{ maxWidth: "400px", margin: "auto", textAlign: "center" }}>
      <h2>üí≥ Mon Portefeuille</h2>
      <h3>Cartes bancaires</h3>
      {cards.map((card, index) => (
        <p key={index}>
          {card.type} - **** **** **** {card.number.slice(-4)} - Exp: {card.expiry} - {card.holder}
        </p>
      ))}

      <div style={{ display: "flex", flexDirection: "column", gap: "10px", alignItems: "center" }}>
        <input
          type="text"
          value={newCardNumber}
          onChange={(e) => setNewCardNumber(e.target.value.replace(/\D/g, "").slice(0, 16))}
          placeholder="Num√©ro de carte (16 chiffres)"
          maxLength="16"
          style={{ padding: "8px", width: "100%" }}
        />
        <input
          type="text"
          value={expiryMonth}
          onChange={(e) => setExpiryMonth(e.target.value.replace(/\D/g, "").slice(0, 2))}
          placeholder="MM"
          maxLength="2"
          style={{ padding: "8px", width: "100%" }}
        />
        <input
          type="text"
          value={expiryYear}
          onChange={(e) => setExpiryYear(e.target.value.replace(/\D/g, "").slice(0, 2))}
          placeholder="YY"
          maxLength="2"
          style={{ padding: "8px", width: "100%" }}
        />
        <input
          type="text"
          value={cvc}
          onChange={(e) => setCvc(e.target.value.replace(/\D/g, "").slice(0, 4))}
          placeholder="CVC (3-4 chiffres)"
          maxLength="4"
          style={{ padding: "8px", width: "100%" }}
        />
        <input
          type="text"
          value={cardHolder}
          onChange={(e) => setCardHolder(e.target.value)}
          placeholder="Nom et pr√©nom du propri√©taire"
          style={{ padding: "8px", width: "100%" }}
        />
        <button onClick={addCard} style={{ padding: "10px", width: "100%", backgroundColor: "#4CAF50", color: "white", border: "none", cursor: "pointer" }}>
          Ajouter
        </button>
      </div>
    </div>
    {/* Liens vers d'autres pages */}
    <h3>üìå Options du compte</h3>
    <p><a href="/account">üë§ Compte</a></p>
    <p><a href="/orders">üì¶ Mes Commandes</a></p>
    <p><a href="/support">üÜò Assistance Client</a></p>
    <p><a href="/addresses">üè† Mes Adresses</a></p>
    
      <button onClick={() => navigate("/products")} style={{ marginTop: "10px", backgroundColor: "gray" }}>
        ‚¨Ö Retour aux Produits
      </button>
    </div>
  );
};

export default WalletPage;
