import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));

    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const addToCart = (product) => {
    const updatedCart = [...cart, {
      _id: product._id,
      name: product.name,
      price: product.price,
      image: product.image
    }];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    alert(`${product.name} a été ajouté au panier !`);
  };

  return (
    <div style={{ 
      backgroundImage: `url('/assets/marvelstoreaccount.jpg')`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      minHeight: "100vh",
      flexDirection: "column",
      padding: "20px",
      color: "#fff",
      display: "flex" }}>
      {/* 🔹 Barre latérale */}
      <nav style={{ width: "80px", backgroundColor: "#333", color: "white", height: "100vh", padding: "10px", position: "fixed" }}>
        <ul style={{ listStyle: "none", padding: 0, display: "flex", flexDirection: "column", alignItems: "center" }}>
          <li><Link to="/" className="nav-link"><Icon icon="mdi:home" width="32" /></Link></li>
          <li><Link to="/products" className="nav-link"><Icon icon="mdi:shopping" width="32" /></Link></li>
          <li><Link to="/cart" className="nav-link"><Icon icon="mdi:cart" width="32" /></Link></li>
          <li><Link to="/account" className="nav-link"><Icon icon="mdi:account" width="32" /></Link></li>
          <li><Link to="/wallet" className="nav-link"><Icon icon="mdi:credit-card" width="32" /></Link></li>
          <li><Link to="/addresses" className="nav-link"><Icon icon="mdi:map-marker" width="32" /></Link></li>
        </ul>
      </nav>
      
      <div style={{ marginLeft: "100px", flex: 1, padding: "20px" }}>
        <h2>🛍️ Nos Produits</h2>
        
        {/* 🔍 Champ de recherche */}
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
        />

        {/* 📄 Lien vers la page du panier */}
        <Link to="/cart" className="bg-blue-500 text-white px-4 py-2 rounded">
          Voir mon panier 🛒 ({cart.length})
        </Link>

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
          {filteredProducts.map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}€</p>
              {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
              <button onClick={() => addToCart(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
                Ajouter au panier
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductsPage;
