import React, { useState } from "react";
import axios from "axios";

const AddProductPage = () => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
  
    reader.onloadend = () => {
      const img = new Image();
      img.src = reader.result;
  
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
  
        // üìå Redimensionner l'image (max 500px de large)
        const maxWidth = 500;
        const scaleSize = maxWidth / img.width;
        canvas.width = maxWidth;
        canvas.height = img.height * scaleSize;
  
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        
        // üìå Convertir en base64 avec qualit√© r√©duite (0.7 = 70% de qualit√©)
        const resizedImage = canvas.toDataURL("image/jpeg", 0.7);
        
        setImage(resizedImage);
        console.log("üü¢ Image redimensionn√©e :", resizedImage.substring(0, 100) + "...");
      };
    };
  
    if (file) {
      reader.readAsDataURL(file);
    }
  };  

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    if (!name || !price || !image) {
      alert("Veuillez remplir tous les champs !");
      return;
    }
  
    console.log("üü¢ Donn√©es envoy√©es au backend :", { name, price, image });
  
    try {
      const response = await axios.post("http://localhost:5000/api/products/add", { name, price, image });
      alert("Produit ajout√© avec succ√®s !");
      console.log("üü¢ R√©ponse du serveur :", response.data);
    } catch (error) {
      console.error("‚ùå Erreur :", error.response?.data?.message || "Probl√®me serveur");
      alert("Erreur lors de l'ajout du produit.");
    }
  };
  

  return (
    <div style={{
        //backgroundColor: "red",
        backgroundImage: `url('/assets/marvelstoreproduits.jpeg')`, // ‚úÖ V√©rifie bien ce chemin
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2>Ajouter un Produit</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nom du produit" value={name} onChange={(e) => setName(e.target.value)} required />
        <input type="number" placeholder="Prix" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <input type="file" accept="image/*" onChange={handleImageChange} required />
        {image && <img src={image} alt="Aper√ßu" style={{ width: "100px", marginTop: "10px" }} />}
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
};

export default AddProductPage;
