import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const [address, setAddress] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cardDetails, setCardDetails] = useState({ number: "", date: "", cvc: "" });
  const [paypalEmail, setPaypalEmail] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = async () => {
    alert("Paiement effectué avec succès ! Votre commande arrivera le " + getDeliveryDate());
    
    // Mise à jour du stock dans la base de données
    await axios.post("http://localhost:5000/api/updateStock", { cart });
    
    // Vider le panier après paiement
    setCart([]);
    localStorage.removeItem("cart");
  };

  return (
    <>
      {/* Barre supérieure + Navigation */}
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          <Link to="/" className="nav-link">
            <Icon icon="mdi:home" width="32" color={location.pathname === "/" ? "red" : "white"} />
          </Link>
          <Link to="/products" className="nav-link">
            <Icon icon="mdi:shopping" width="32" color={location.pathname === "/products" ? "red" : "white"} />
          </Link>
          <Link to="/cart" className="nav-link">
            <Icon icon="mdi:cart" width="32" color={location.pathname === "/cart" ? "red" : "white"} />
          </Link>
          <Link to="/account" className="nav-link">
            <Icon icon="mdi:account" width="32" color={location.pathname === "/account" ? "red" : "white"} />
          </Link>
          <Link to="/wallet" className="nav-link">
            <Icon icon="mdi:credit-card" width="32" color={location.pathname === "/wallet" ? "red" : "white"} />
          </Link>
          <Link to="/addresses" className="nav-link">
            <Icon icon="mdi:map-marker" width="32" color={location.pathname === "/addresses" ? "red" : "white"} />
          </Link>
        </nav>
      </header>

      <main style={{ marginTop: "60px" }}>
        <h2>🛒 Mon Panier</h2>

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
          {cart.map((product, index) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#222", color: "#fff" }}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}€</p>
              {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
              <button onClick={() => removeFromCart(index)} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px", marginTop: "10px" }}>Supprimer</button>
            </div>
          ))}
        </div>

        {cart.length > 0 && (
          <div style={{ backgroundColor: "#222", padding: "20px", borderRadius: "10px", marginTop: "20px" }}>
            <h3>💳 Paiement</h3>
            <label>Adresse de livraison:</label>
            <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Votre adresse complète" />
            
            <label>Mode de paiement:</label>
            <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)}>
              <option value="card">Carte bancaire</option>
              <option value="paypal">PayPal</option>
            </select>

            {paymentMethod === "card" && (
              <>
                <input type="text" value={cardDetails.number} onChange={(e) => setCardDetails({ ...cardDetails, number: e.target.value })} placeholder="Numéro de carte" />
                <input type="text" value={cardDetails.date} onChange={(e) => setCardDetails({ ...cardDetails, date: e.target.value })} placeholder="Date d'expiration" />
                <input type="text" value={cardDetails.cvc} onChange={(e) => setCardDetails({ ...cardDetails, cvc: e.target.value })} placeholder="CVC" />
              </>
            )}

            {paymentMethod === "paypal" && (
              <input type="email" value={paypalEmail} onChange={(e) => setPaypalEmail(e.target.value)} placeholder="Email PayPal" />
            )}

            <p>📅 Date de livraison prévue: <strong>{getDeliveryDate()}</strong></p>
            
            <button onClick={handlePayment} style={{ backgroundColor: "green", color: "white", padding: "10px", borderRadius: "5px", marginTop: "10px" }}>Effectuer le paiement</button>
          </div>
        )}
      </main>
    </>
  );
};

export default CartPage;
