import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function AdminUsersPage() {
  const API_URL = "http://localhost:5000/api";
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const email = localStorage.getItem("email"); // On récupère l'email du localStorage
    if (email !== "rngasseu123@gmail.com") {
      navigate("/"); // Si ce n'est pas Rayan, on renvoie à la page d'accueil
      return;
    }

    fetch(`${API_URL}/users`)
      .then((res) => res.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error("Erreur lors de la récupération des utilisateurs:", error));
  }, [navigate]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Gestion des Utilisateurs</h2>

      {users.length > 0 ? (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ backgroundColor: "#f5f5f5" }}>
              <th style={{ border: "1px solid #ddd", padding: "8px" }}>Nom</th>
              <th style={{ border: "1px solid #ddd", padding: "8px" }}>Email</th>
              <th style={{ border: "1px solid #ddd", padding: "8px" }}>Rôle</th>
              <th style={{ border: "1px solid #ddd", padding: "8px" }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td style={{ border: "1px solid #ddd", padding: "8px" }}>{user.name}</td>
                <td style={{ border: "1px solid #ddd", padding: "8px" }}>{user.email}</td>
                <td style={{ border: "1px solid #ddd", padding: "8px" }}>{user.isAdmin ? "Admin" : "Client"}</td>
                <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                  <button style={{ marginRight: "10px" }}>Modifier</button>
                  <button style={{ color: "red" }}>Supprimer</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Aucun utilisateur trouvé.</p>
      )}
    </div>
  );
}

export default AdminUsersPage;
