import React, { useState, useEffect } from "react";
import { useNavigate, Link, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
    } else {
      setUser(storedUser);
      fetchOrders(storedUser.token);
    }
  }, [navigate]);

  const fetchOrders = async (token) => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:5000/api/orders", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setOrders(response.data);
    } catch (error) {
      console.error("‚ùå Erreur lors de la r√©cup√©ration des commandes:", error);
      alert("Erreur lors du chargement des commandes.");
    } finally {
      setLoading(false);
    }
  };

  const reorder = async (orderId) => {
    try {
      await axios.post(
        "http://localhost:5000/api/orders/reorder",
        { orderId },
        { headers: { Authorization: `Bearer ${user.token}` } }
      );
      alert("Commande repass√©e avec succ√®s !");
    } catch (error) {
      console.error("‚ùå Erreur lors de la commande :", error);
      alert("Impossible de repasser la commande.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        backgroundImage: "url('/assets/marvelstoreproduit.jpeg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
      }}
    >
      {/* üîπ Barre de navigation */}
      <header
        style={{
          display: "flex",
          flexDirection: "column",
          backgroundColor: "#222",
          color: "white",
          padding: "10px 1px",
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          zIndex: 10,
        }}
      >
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          {[
            { path: "/", icon: "mdi:home" },
            { path: "/products", icon: "mdi:shopping" },
            { path: "/cart", icon: "mdi:cart" },
            { path: "/orders", icon: "mdi:package-variant-closed" },
            { path: "/account", icon: "mdi:account" },
            { path: "/wallet", icon: "mdi:credit-card" },
            { path: "/addresses", icon: "mdi:map-marker" },
          ].map((item) => (
            <Link key={item.path} to={item.path} className="nav-link">
              <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
            </Link>
          ))}
        </nav>

        {/* üîπ Barre utilisateur */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>üõçÔ∏è Faites-vous plaisir</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
            {user ? (
              <>
                <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
                <button
                  onClick={handleLogout}
                  style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}
                >
                  D√©connexion
                </button>
              </>
            ) : (
              <button
                onClick={() => navigate("/login")}
                style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}
              >
                Login
              </button>
            )}
          </div>
        </div>
      </header>

      {/* üì¶ Liste des commandes */}
      <div style={{ maxWidth: "600px", margin: "auto", padding: "80px", backgroundColor: "#fff", borderRadius: "10px", boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)", marginTop: "80px" }}>
        <h2 style={{ textAlign: "center", color: "#333" }}>üì¶ Mes Commandes</h2>

        {loading ? (
          <p>Chargement...</p>
        ) : orders.length === 0 ? (
          <p>Aucune commande trouv√©e.</p>
        ) : (
          orders.map((order) => (
            <div key={order.id} style={{ border: "1px solid #ddd", padding: "10px", marginBottom: "10px", borderRadius: "5px" }}>
              <p>Commande #{order.id} - Statut: {order.status}</p>
              {order.status === "Livr√©e" && (
                <button
                  onClick={() => reorder(order.id)}
                  style={{ backgroundColor: "#007bff", color: "white", padding: "5px", borderRadius: "3px", cursor: "pointer" }}
                >
                  Acheter √† nouveau
                </button>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default OrdersPage;
