import React, { useState, useEffect } from "react";
import { useNavigate, Link, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";


const WalletPage = () => {
  const [cards, setCards] = useState([]);
  const [newCardNumber, setNewCardNumber] = useState("");
  const [expiryMonth, setExpiryMonth] = useState("");
  const [expiryYear, setExpiryYear] = useState("");
  const [cvc, setCvc] = useState("");
  const [cardHolder, setCardHolder] = useState("");
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();
  

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
    } else {
      setUser(storedUser);
      fetchCards(storedUser.token);
    }
  }, [navigate]);

  // üîπ R√©cup√©rer les cartes de l'utilisateur
  const fetchCards = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/cards", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      setCards(data);
    } catch (error) {
      console.error("Erreur r√©cup√©ration cartes", error);
    }
  };

  // üîπ Ajouter une carte
  const addCard = async () => {
    if (cards.length >= 2) {
      alert("Vous avez d√©j√† 2 cartes, supprimez-en une avant d'ajouter une nouvelle.");
      return;
    }

    if (newCardNumber.length === 16 && expiryMonth.length === 2 && expiryYear.length === 2 && (cvc.length === 3 || cvc.length === 4) && cardHolder.trim() !== "") {
      try {
        const response = await fetch("http://localhost:5000/api/cards", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify({
            number: newCardNumber,
            expiryMonth,
            expiryYear,
            cvc,
            holder: cardHolder,
          }),
        });

        const newCard = await response.json();
        setCards([...cards, newCard]);

        // R√©initialiser le formulaire
        setNewCardNumber("");
        setExpiryMonth("");
        setExpiryYear("");
        setCvc("");
        setCardHolder("");
      } catch (error) {
        console.error("Erreur ajout carte", error);
      }
    } else {
      alert("Veuillez remplir correctement tous les champs.");
    }
  };

  // üîπ Supprimer une carte
  const deleteCard = async (cardId) => {
    try {
      await fetch(`http://localhost:5000/api/cards/${cardId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${user.token}` },
      });
      setCards(cards.filter((card) => card._id !== cardId));
    } catch (error) {
      console.error("Erreur suppression carte", error);
    }
  };

  // üîπ D√©connexion
  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div style={{ backgroundColor: "#f5f5f5", minHeight: "100vh", paddingTop: "80px" }}>
      {/* Barre sup√©rieure */}
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        
        {/* Navigation */}
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          {[{ path: "/", icon: "mdi:home" }, { path: "/products", icon: "mdi:shopping" }, { path: "/cart", icon: "mdi:cart" }, { path: "/account", icon: "mdi:account" }, { path: "/wallet", icon: "mdi:credit-card" }, { path: "/addresses", icon: "mdi:map-marker" }]
            .map((item) => (
              <Link key={item.path} to={item.path} className="nav-link">
                <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
              </Link>
            ))}
        </nav>

        {/* Infos utilisateur */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>üõçÔ∏è Passer √† la caisse</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
            {user ? (
              <>
                <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>D√©connexion</button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
            )}
          </div>
        </div>
      </header>

      {/* Contenu principal */}
      <div style={{ maxWidth: "600px", margin: "auto", padding: "20px", backgroundColor: "#fff", borderRadius: "10px", boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.1)", marginTop: "40px" }}>
        <h2 style={{ textAlign: "center", color: "#333" }}>üí≥ Mon Portefeuille</h2>

        {cards.map((card) => (
          <div key={card._id} style={{ border: "1px solid #ddd", borderRadius: "8px", padding: "10px", marginBottom: "10px" }}>
            <p>**** **** **** {card.number.slice(-4)}</p>
            <p>Expiration: {card.expiryMonth}/{card.expiryYear} - {card.holder}</p>
            <button onClick={() => deleteCard(card._id)} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "5px" }}>
              Supprimer
            </button>
          </div>
        ))}

        {cards.length < 2 && (
          <div style={{ display: "flex", flexDirection: "column", gap: "10px", marginTop: "20px" }}>
            <h3 style={{ textAlign: "center" }}>Ajouter une carte</h3>
            <input type="text" value={newCardNumber} onChange={(e) => setNewCardNumber(e.target.value.replace(/\D/g, "").slice(0, 16))} placeholder="Num√©ro de carte (16 chiffres)" maxLength="16" />
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <input type="text" value={expiryMonth} onChange={(e) => setExpiryMonth(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="MM" maxLength="2" style={{ width: "48%" }} />
              <input type="text" value={expiryYear} onChange={(e) => setExpiryYear(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="YY" maxLength="2" style={{ width: "48%" }} />
            </div>
            <input type="text" value={cvc} onChange={(e) => setCvc(e.target.value.replace(/\D/g, "").slice(0, 4))} placeholder="CVC (3-4 chiffres)" maxLength="4" />
            <input type="text" value={cardHolder} onChange={(e) => setCardHolder(e.target.value)} placeholder="Nom et pr√©nom du propri√©taire" />
            <button onClick={addCard} style={{ backgroundColor: "#4CAF50", color: "white", padding: "10px", borderRadius: "5px" }}>
              Ajouter
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default WalletPage;
