import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(() => JSON.parse(localStorage.getItem("user")) || null);
  const [address, setAddress] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const fetchCart = async () => {
      if (!user) return;
      try {
        const response = await axios.get(`http://localhost:5000/api/cart/${user._id}`);
        setCart(response.data.products);
      } catch (error) {
        console.error("Erreur de récupération du panier :", error);
      }
    };
  
    fetchCart();
  }, [user]);
  

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = async () => {
    if (!user) return alert("Vous devez être connecté");

    try {
      await axios.post("http://localhost:5000/api/updateStock", { cart });
      alert(`Paiement effectué avec succès ! Votre commande arrivera le ${getDeliveryDate()}`);
      setCart([]);
    } catch (error) {
      console.error("Erreur lors du paiement :", error);
      alert("Une erreur est survenue lors du paiement. Veuillez réessayer.");
    }
  };

  return (
    <div>
      <header style={{ display: "flex", justifyContent: "space-between", backgroundColor: "#222", color: "white", padding: "10px" }}>
        <h1>🛍️ Passer à la caisse</h1>
        <div>
          {user ? (
            <>
              <span>{user.name}</span>
              <button onClick={() => { localStorage.removeItem("user"); setUser(null); navigate("/login"); }} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>Déconnexion</button>
            </>
          ) : (
            <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
          )}
        </div>
      </header>

      <main style={{ padding: "20px" }}>
        <h2>🛒 Mon Panier</h2>

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
          {cart.map((item, index) => (
            <div key={item.productId._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#222", color: "#fff" }}>
              <h3>{item.productId.name}</h3>
              <p>Prix : {item.productId.price}€</p>
              <p>Quantité : {item.quantity}</p>
              <button onClick={() => removeFromCart(item.productId._id)} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px", marginTop: "10px" }}>Supprimer</button>
            </div>
          ))}
        </div>

        {cart.length > 0 && (
          <button onClick={handlePayment} style={{ backgroundColor: "green", color: "white", padding: "10px", borderRadius: "5px", marginTop: "10px" }}>Effectuer le paiement</button>
        )}
      </main>
    </div>
  );
};

export default CartPage;
