import React, { useState } from "react";
import axios from "axios";

const AddProductPage = () => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    
    reader.onloadend = () => {
      setImage(reader.result); // ✅ Convertit l'image en base64
    };
    
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:5000/api/products/add", { name, price, image });
      alert("Produit ajouté avec succès !");
      console.log(response.data);
    } catch (error) {
      console.error("Erreur :", error);
      alert("Erreur lors de l'ajout du produit.");
    }
  };

  return (
    <div>
      <h2>Ajouter un Produit</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nom du produit" value={name} onChange={(e) => setName(e.target.value)} required />
        <input type="number" placeholder="Prix" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <input type="file" accept="image/*" onChange={handleImageChange} required />
        {image && <img src={image} alt="Aperçu" style={{ width: "100px", marginTop: "10px" }} />}
        <button type="submit">Ajouter</button>
      </form>
    </div>
  );
};

export default AddProductPage;
