import React, { useState, useEffect } from "react";
import { useNavigate, Link, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (!storedUser) {
      navigate("/login");
    } else {
      setUser(storedUser);
      fetchOrders(storedUser._id);
    }
  }, [navigate]);

  const fetchOrders = async (userId) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/orders/user/${userId}`);
      setOrders(response.data);
    } catch (error) {
      console.error("Error retrieving orders:", error);
      alert("Error loading orders.");
    } finally {
      setLoading(false);
    }
  };

  const cancelOrder = async (orderId) => {
    try {
      await axios.delete(`http://localhost:5000/api/orders/${orderId}`);
      setOrders((prevOrders) => prevOrders.filter(order => order._id !== orderId));
    } catch (error) {
      console.error("Error cancelling:", error);
      alert("Error cancelling the order.");
    }
  };

  const getDeliveryDate = (createdAt) => {
    const date = new Date(createdAt);
    date.setDate(date.getDate() + 2);
    return date.toLocaleDateString("en-GB", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      {/* Left Sidebar */}
      <aside style={{
        width: "250px",
        backgroundColor: "#222",
        color: "white",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        padding: "20px 10px",
        position: "fixed",
        top: 0,
        bottom: 0,
        left: 0
      }}>
        <div>
          <h2 style={{ marginBottom: "30px", textAlign: "center" }}>🛍 The Shop</h2>
          <nav style={{ display: "flex", flexDirection: "column", gap: "20px", alignItems: "center" }}>
            {[
              { path: "/", icon: "mdi:home" },
              { path: "/products", icon: "mdi:shopping" },
              { path: "/cart", icon: "mdi:cart" },
              { path: "/orders", icon: "mdi:package-variant-closed" },
              { path: "/account", icon: "mdi:account" },
              { path: "/addresses", icon: "mdi:map-marker" },
            ].map((item) => (
              <Link key={item.path} to={item.path} style={{ textDecoration: "none" }}>
                <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
              </Link>
            ))}
          </nav>
        </div>

        {/* User Info */}
        <div style={{ marginTop: "40px", textAlign: "center" }}>
          {user && (
            <>
              <Icon icon="mdi:account-circle" width="40" />
              <div style={{ margin: "10px 0" }}>{user.name}</div>
              <button onClick={handleLogout} style={{
                backgroundColor: "red",
                color: "white",
                border: "none",
                padding: "5px 10px",
                borderRadius: "5px",
                cursor: "pointer"
              }}>
                Logout
              </button>
            </>
          )}
        </div>
      </aside>

      {/* Main Content */}
      <main style={{ marginLeft: "250px", padding: "30px", flex: 1 }}>
        <h1 style={{ marginBottom: "20px" }}>📦 My Orders</h1>
        {loading ? (
          <p>Loading...</p>
        ) : orders.length === 0 ? (
          <p>No orders found.</p>
        ) : (
          <div style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
            gap: "20px"
          }}>
            {orders.map((order) => (
              <div key={order._id} style={{
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "8px",
                backgroundColor: "#f9f9f9"
              }}>
                <p><strong>ID:</strong> {order._id}</p>
                <p><strong>Total:</strong> {order.total || "-"} €</p>
                <p><strong>Address:</strong> {order.address?.street}, {order.address?.city}</p>
                <p><strong>Estimated delivery:</strong> {getDeliveryDate(order.createdAt)}</p>

                <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                  <span><strong>Status:</strong> {order.status}</span>
                  <span style={{
                    display: "inline-block",
                    width: "12px",
                    height: "12px",
                    borderRadius: "50%",
                    backgroundColor:
                      order.status === "Livery"
                        ? "green"
                        : new Date(order.deliveryDate) <= new Date()
                          ? "blue"
                          : "orange"
                  }}></span>
                </div>

                {order.items.map((item, idx) => (
                  <div key={idx} style={{ marginLeft: "10px", marginTop: "5px" }}>
                    <p>- Product ID: {item.productId}</p>
                    <p>Quantity: {item.quantity} / Color: {item.color} / Size: {item.size}</p>
                  </div>
                ))}

                {order.status === "In progress" &&
                  new Date(order.deliveryDate) - new Date() > 24 * 60 * 60 * 1000 && (
                    <button onClick={() => cancelOrder(order._id)} style={{
                      marginTop: "10px",
                      padding: "5px",
                      backgroundColor: "orange",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer"
                    }}>
                      Cancel order
                    </button>
                  )}
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
};

export default OrdersPage;
