import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const AccountPage = () => {
  const [user, setUser] = useState({ name: "", email: "", password: "", number: "" });
  const [editMode, setEditMode] = useState({ name: false, email: false, password: false, number: false });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser || !storedUser.email) {
          console.error("❌ Aucun utilisateur trouvé !");
          return;
        }

        const response = await axios.get(`http://localhost:5000/api/users/user/${storedUser.email}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setUser(response.data);
      } catch (error) {
        console.error("❌ Erreur lors du chargement du compte :", error);
      }
    };
    fetchUser();
  }, []);

  const handleEditToggle = (field) => {
    setEditMode((prev) => ({ ...prev, [field]: !prev[field] }));
  };

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/users/user/${user.email}`, user, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      alert("✅ Modifications enregistrées !");
    } catch (error) {
      console.error("❌ Erreur lors de la sauvegarde :", error);
    }
  };
  

  return (
    <div style={{
      backgroundImage: `url('/assets/marvelstoreaccount.jpg')`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      textAlign: "center",
      padding: "20px",
      color: "#fff"
    }}>
      <h2>👤 Mon Compte</h2>
      {message && <p style={{ color: message.includes("❌") ? "red" : "green" }}>{message}</p>}

      {/* Nom */}
      <label>Nom:</label>
      {editMode.name ? (
        <input type="text" name="name" value={user.name} onChange={handleChange} />
      ) : (
        <p>{user.name} <button onClick={() => handleEditToggle("name")}>Modifier</button></p>
      )}

      {/* Email */}
      <label>Email:</label>
      <p>{user.email} (non modifiable)</p>

      {/* Mot de passe */}
      <label>Mot de passe:</label>
      {editMode.password ? (
        <input type="password" name="password" value={user.password} onChange={handleChange} />
      ) : (
        <p>******** <button onClick={() => handleEditToggle("password")}>Modifier</button></p>
      )}

      {/* Numéro de téléphone */}
      <label>Numéro de téléphone:</label>
      {editMode.number ? (
        <input type="text" name="number" value={user.number} onChange={handleChange} />
      ) : (
        <p>{user.number || "Non renseigné"} <button onClick={() => handleEditToggle("number")}>Modifier</button></p>
      )}

      <button onClick={handleSave} style={{ marginTop: "10px" }}>💾 Enregistrer</button>

      {/* Liens vers d'autres pages */}
      <div style={{ marginTop: "20px", textAlign: "left" }}>
        <h3>📌 Options du compte</h3>
        <p><a href="/orders">📦 Mes Commandes</a></p>
        <p><a href="/support">🆘 Assistance Client</a></p>
        <p><a href="/wallet">💳 Mon Portefeuille</a></p>
        <p><a href="/addresses">🏠 Mes Adresses</a></p>
      </div>

      <button onClick={() => navigate("/products")} style={{ marginTop: "10px", backgroundColor: "gray" }}>
        ⬅ Retour aux Produits
      </button>
    </div>
  );
};

export default AccountPage;
