import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });

      if (response.data.success) {
        alert("Connexion réussie !");
        navigate("/dashboard"); // 🔄 Redirige vers le tableau de bord après connexion
      }
    } catch (error) {
      setError("❌ Email ou mot de passe incorrect.");
    }
  };

  return (
    <div
      className="min-h-screen flex flex-col items-center justify-center bg-cover bg-center px-4"
      style={{ backgroundImage: `url('/assets/marvelstoreproduits.jpeg')`,
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
       }}
    >
      {/* 🔹 Bouton pour revenir à l'accueil */}
      <div className="absolute top-4 right-6">
        <Link to="/" className="text-white text-lg font-bold hover:underline">
          Accueil
        </Link>
      </div>

      {/* 🔹 Conteneur de la boîte de connexion */}
      <div className="bg-black bg-opacity-70 p-8 rounded-lg shadow-lg text-white w-full max-w-md">
        <h2 className="text-3xl font-bold text-center mb-6">Bienvenue !</h2>

        {error && <p className="text-red-500 text-center">{error}</p>}

        <form onSubmit={handleSubmit} className="flex flex-col">
          <label className="mb-1">Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-3 mb-4 rounded bg-white text-black"
            required
          />

          <label className="mb-1">Mot de passe</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="p-3 mb-4 rounded bg-white text-black"
            required
          />

          {/* 🔹 Checkbox "Se souvenir de moi" */}
          <div className="flex items-center mb-4">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
              className="mr-2"
            />
            <label>Se souvenir de moi</label>
          </div>

          {/* 🔹 Bouton Connexion */}
          <button type="submit" className="bg-green-700 hover:bg-green-800 p-3 rounded text-white font-bold mb-4">
            ENTRER
          </button>
        </form>

        {/* 🔹 Lien "Mot de passe oublié" */}
        <div className="text-center mb-2">
          <Link to="/forgot-password" className="text-white hover:underline">
            Mot de passe oublié ?
          </Link>
        </div>

        {/* 🔹 Lien "S'inscrire" */}
        <div className="text-center">
          <span className="text-gray-300">Pas encore inscrit ? </span>
          <Link to="/register" className="text-blue-400 hover:underline">
            S'inscrire
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
