import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // ‚úÖ Importer useNavigate pour rediriger

const LoginPage = () => {
  const navigate = useNavigate(); // ‚úÖ Hook pour la redirection
  const [isRegistering, setIsRegistering] = useState(false);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isRegistering ? "/register" : "/login";

    try {
      const response = await axios.post(`http://localhost:5000/api/users${endpoint}`, { name, email, password });
      alert(response.data.message);

      if (!isRegistering) {
        localStorage.setItem("token", response.data.token); // ‚úÖ Sauvegarde du token si besoin
        navigate("/products"); // ‚úÖ Redirection apr√®s connexion r√©ussie
      } else {
        setIsRegistering(false); // ‚úÖ Passer √† la page de connexion apr√®s inscription
      }
    } catch (error) {
      console.error("Erreur :", error.response?.data?.message || "Probl√®me serveur");
      alert("Erreur : " + error.response?.data?.message || "Probl√®me serveur");
    }

    if (!email || !password) {
        alert("Veuillez remplir tous les champs !");
        return;
      }
    
      console.log("üü¢ Donn√©es envoy√©es au backend :", { email, password });
      
    try {
        const response = await axios.post("http://localhost:5000/api/users/login", { email, password });
        alert("Connexion r√©ussie !");
        console.log("üü¢ R√©ponse du serveur :", response.data);
      } catch (error) {
        console.error("‚ùå Erreur :", error.response?.data?.message || "Probl√®me serveur");
        alert("Erreur lors de la connexion.");
      }
  };

  return (
    <div style={{
        //backgroundColor: "red",
        backgroundImage: `url('/assets/marvelstore.webp')`, // ‚úÖ V√©rifie bien ce chemin
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2>{isRegistering ? "S'inscrire" : "Se connecter"}</h2>
      <form onSubmit={handleSubmit}>
        {isRegistering && (
          <input type="text" placeholder="Nom" value={name} onChange={(e) => setName(e.target.value)} required />
        )}
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Mot de passe" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">{isRegistering ? "S'inscrire" : "Se connecter"}</button>
      </form>
      <p onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? "D√©j√† un compte ? Connectez-vous" : "Pas encore de compte ? Inscrivez-vous"}
      </p>
    </div>
  );
};

export default LoginPage;
