import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  // üîç Filtrage des produits en fonction du champ de recherche
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // üõí Ajouter un produit au panier et le sauvegarder
  const addToCart = (product) => {
    const updatedCart = [...cart, {
      _id: product._id,
      name: product.name,
      price: product.price,
      image: product.image // ‚úÖ On stocke aussi l'image !
    }];
  
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart)); // ‚úÖ Sauvegarde
    alert(`${product.name} a √©t√© ajout√© au panier !`);
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/marvelstoreproduits.jpeg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2>üõçÔ∏è Nos Produits</h2>

      {/* üîç Champ de recherche */}
      <input
        type="text"
        placeholder="Rechercher un produit..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
      />

      {/* üìÑ Lien vers la page du panier */}
      <Link to="/cart" className="bg-blue-500 text-white px-4 py-2 rounded">
        Voir mon panier üõí ({cart.length})
      </Link>

      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
        {filteredProducts.map((product) => (
          <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
            <h3>{product.name}</h3>
            <p>Prix : {product.price}‚Ç¨</p>
            {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
            <button onClick={() => addToCart(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
              Ajouter au panier
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
