import React, { useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";

const WalletPage = () => {
  const [cards, setCards] = useState([]);
  const [newCardNumber, setNewCardNumber] = useState("");
  const [expiryMonth, setExpiryMonth] = useState("");
  const [expiryYear, setExpiryYear] = useState("");
  const [cvc, setCvc] = useState("");
  const [cardHolder, setCardHolder] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  const detectCardType = (number) => {
    const visaRegex = /^4[0-9]{12}(?:[0-9]{3})?$/;
    const masterCardRegex = /^5[1-5][0-9]{14}$/;
    if (visaRegex.test(number)) return "Visa";
    if (masterCardRegex.test(number)) return "Mastercard";
    return "Inconnu";
  };

  const addCard = () => {
    if (
      newCardNumber.length === 16 &&
      expiryMonth.length === 2 &&
      expiryYear.length === 2 &&
      (cvc.length === 3 || cvc.length === 4) &&
      cardHolder.trim() !== ""
    ) {
      const cardType = detectCardType(newCardNumber);
      const newCard = {
        number: newCardNumber,
        type: cardType,
        expiry: `${expiryMonth}/${expiryYear}`,
        cvc,
        holder: cardHolder,
      };

      setCards([...cards, newCard]);
      setNewCardNumber("");
      setExpiryMonth("");
      setExpiryYear("");
      setCvc("");
      setCardHolder("");
    } else {
      alert("Veuillez remplir correctement tous les champs.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div
      style={{
        backgroundImage: `url('/assets/marvelstorewallet.jpeg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        color: "#fff",
      }}
    >
      {/* ðŸ”¹ Barre supÃ©rieure + Navigation */}
      <header
        style={{
          display: "flex",
          flexDirection: "column",
          backgroundColor: "#222",
          color: "white",
          padding: "10px 1px",
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          zIndex: 10,
        }}
      >
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          <Link to="/" className="nav-link">
            <Icon icon="mdi:home" width="32" color={location.pathname === "/" ? "red" : "white"} />
          </Link>
          <Link to="/products" className="nav-link">
            <Icon icon="mdi:shopping" width="32" color={location.pathname === "/products" ? "red" : "white"} />
          </Link>
          <Link to="/cart" className="nav-link">
            <Icon icon="mdi:cart" width="32" color={location.pathname === "/cart" ? "red" : "white"} />
          </Link>
          <Link to="/account" className="nav-link">
            <Icon icon="mdi:account" width="32" color={location.pathname === "/account" ? "red" : "white"} />
          </Link>
          <Link to="/wallet" className="nav-link">
            <Icon icon="mdi:credit-card" width="32" color={location.pathname === "/wallet" ? "red" : "white"} />
          </Link>
          <Link to="/addresses" className="nav-link">
            <Icon icon="mdi:map-marker" width="32" color={location.pathname === "/addresses" ? "red" : "white"} />
          </Link>
        </nav>

        {/* ðŸ”¹ Barre utilisateur */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>ðŸ’³ Mon Portefeuille</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
            {localStorage.getItem("user") ? (
              <>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>
                  DÃ©connexion
                </button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>
                Login
              </button>
            )}
          </div>
        </div>
      </header>

      {/* ðŸ”¹ Contenu principal */}
      <div style={{ marginTop: "100px", padding: "20px", textAlign: "center", width: "100%", maxWidth: "400px" }}>
        <h2>Cartes bancaires</h2>
        {cards.map((card, index) => (
          <p key={index}>
            {card.type === "Visa" && <Icon icon="mdi:credit-card-outline" width="24" color="blue" />}
            {card.type === "Mastercard" && <Icon icon="mdi:credit-card-multiple" width="24" color="red" />}
            {card.type} - **** **** **** {card.number.slice(-4)} - Exp: {card.expiry} - {card.holder}
          </p>
        ))}

        {/* ðŸ”¹ Formulaire d'ajout de carte */}
        <div style={{ display: "flex", flexDirection: "column", gap: "10px", alignItems: "center", backgroundColor: "rgba(0,0,0,0.7)", padding: "20px", borderRadius: "10px" }}>
          <input type="text" value={newCardNumber} onChange={(e) => setNewCardNumber(e.target.value.replace(/\D/g, "").slice(0, 16))} placeholder="NumÃ©ro de carte (16 chiffres)" maxLength="16" style={{ padding: "8px", width: "100%" }} />
          <input type="text" value={expiryMonth} onChange={(e) => setExpiryMonth(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="MM" maxLength="2" style={{ padding: "8px", width: "100%" }} />
          <input type="text" value={expiryYear} onChange={(e) => setExpiryYear(e.target.value.replace(/\D/g, "").slice(0, 2))} placeholder="YY" maxLength="2" style={{ padding: "8px", width: "100%" }} />
          <input type="text" value={cvc} onChange={(e) => setCvc(e.target.value.replace(/\D/g, "").slice(0, 4))} placeholder="CVC (3-4 chiffres)" maxLength="4" style={{ padding: "8px", width: "100%" }} />
          <input type="text" value={cardHolder} onChange={(e) => setCardHolder(e.target.value)} placeholder="Nom et prÃ©nom du propriÃ©taire" style={{ padding: "8px", width: "100%" }} />
          <button onClick={addCard} style={{ padding: "10px", width: "100%", backgroundColor: "#4CAF50", color: "white", border: "none", cursor: "pointer" }}>Ajouter</button>
        </div>

        <button onClick={() => navigate("/products")} style={{ marginTop: "20px", backgroundColor: "gray", color: "white", padding: "10px", borderRadius: "5px", width: "100%" }}>â¬… Retour aux Produits</button>
      </div>
    </div>
  );
};

export default WalletPage;
