import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState("");
  const [selectedSize, setSelectedSize] = useState("");
  const [quantity, setQuantity] = useState(1);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const openModal = (product) => {
    setSelectedProduct(product);
    setSelectedColor("");
    setSelectedSize("");
    setQuantity(1);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  const handleAddToCart = () => {
    if (!selectedColor || (selectedProduct.category !== "informatique" && !selectedSize) || quantity < 1) return;

    const updatedCart = [...cart, {
      _id: selectedProduct._id,
      name: selectedProduct.name,
      price: selectedProduct.price,
      image: selectedProduct.image,
      color: selectedColor,
      size: selectedSize,
      quantity: quantity
    }];

    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    closeModal();
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{
      display: "flex",
      flexDirection: "column",
      minHeight: "100vh",
      backgroundImage: "url('/assets/marvelstoreproduit.jpeg')",
      backgroundSize: "cover",
      backgroundPosition: "center",
      color: "white"
    }}>
      {/* Barre supérieure */}
      <header style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        backgroundColor: "#222",
        padding: "10px 20px",
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        zIndex: 10
      }}>
        <h1>🛍️ Marvel Store</h1>

        {/* Icône utilisateur */}
        {user ? (
          <div style={{ display: "flex", alignItems: "center", cursor: "pointer", position: "relative" }} onClick={() => setUserMenuOpen(!userMenuOpen)}>
            <Icon icon="mdi:account-circle" width="40" color="red" />
            <span style={{ marginLeft: "10px" }}>{user.name}</span>

            {userMenuOpen && (
              <div style={{
                position: "absolute",
                top: "50px",
                right: "0",
                backgroundColor: "white",
                color: "black",
                padding: "10px",
                borderRadius: "5px",
                boxShadow: "0px 0px 10px rgba(0,0,0,0.1)"
              }}>
                <p><strong>Nom :</strong> {user.name}</p>
                <p><strong>Téléphone :</strong> {user.phone}</p>
                <button onClick={handleLogout} style={{
                  backgroundColor: "red",
                  color: "white",
                  padding: "5px",
                  borderRadius: "3px",
                  width: "100%"
                }}>Déconnexion</button>
              </div>
            )}
          </div>
        ) : (
          <button onClick={() => navigate("/login")} style={{ backgroundColor: "blue", color: "white", padding: "5px 10px", borderRadius: "3px" }}>
            Login
          </button>
        )}
      </header>

      <div style={{ marginTop: "80px", padding: "20px" }}>
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "10px", width: "300px" }}
        />

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
          {filteredProducts.map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff", cursor: "pointer" }}
              onClick={() => openModal(product)}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}€</p>
              {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
              <button onClick={(e) => { e.stopPropagation(); openModal(product); }} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
                Ajouter au panier
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Fenêtre modale */}
      {selectedProduct && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100%", height: "100%",
          backgroundColor: "rgba(0,0,0,0.7)", display: "flex", justifyContent: "center", alignItems: "center"
        }}>
          <div style={{ backgroundColor: "white", color: "black", padding: "20px", borderRadius: "10px", width: "400px" }}>
            <h2>{selectedProduct.name}</h2>
            <p>{selectedProduct.description}</p>
            <img src={selectedProduct.image} alt={selectedProduct.name} style={{ width: "100%" }} />

            <label>Couleur :</label>
            <select value={selectedColor} onChange={(e) => setSelectedColor(e.target.value)}>
              <option value="">Sélectionnez une couleur</option>
              <option value="Rouge">Rouge</option>
              <option value="Bleu">Bleu</option>
              <option value="Noir">Noir</option>
              <option value="Blanc">Blanc</option>
            </select>

            {selectedProduct.category !== "informatique" && (
              <>
                <label>Taille :</label>
                <select value={selectedSize} onChange={(e) => setSelectedSize(e.target.value)}>
                  <option value="">Sélectionnez une taille</option>
                  {selectedProduct.category === "chaussures"
                    ? Array.from({ length: 10 }, (_, i) => 36 + i).map(size => <option key={size} value={size}>{size}</option>)
                    : ["S", "M", "L", "XL", "XXL"].map(size => <option key={size} value={size}>{size}</option>)}
                </select>
              </>
            )}

            <button onClick={handleAddToCart} disabled={!selectedColor || (!selectedSize && selectedProduct.category !== "informatique")}>
              Ajouter au panier
            </button>
            <button onClick={closeModal}>Fermer</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
