import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  // ✅ Chargement des produits & de l'utilisateur connecté
  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));

    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) setUser(storedUser);
  }, []);

  // ✅ Déconnexion
  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  // ✅ Filtrage des produits en fonction de la recherche
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // ✅ Ajouter un produit au panier
  const addToCart = (product) => {
    const updatedCart = [...cart, {
      _id: product._id,
      name: product.name,
      price: product.price,
      image: product.image
    }];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    alert(`${product.name} a été ajouté au panier !`);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* 🔹 Barre de navigation */}
      <div className="flex justify-between items-center bg-white shadow-md p-4 fixed top-0 left-0 w-full z-50">
        <h1 className="text-2xl font-bold">🛍️ Produits</h1>

        {/* ✅ Profil utilisateur aligné à droite */}
        <div className="relative">
          <button onClick={() => setMenuOpen(!menuOpen)} className="flex items-center space-x-2">
            <img
              src="https://cdn-icons-png.flaticon.com/512/847/847969.png"
              alt="User"
              className="w-6 h-6 rounded-full border border-gray-300"
            />
          </button>

          {/* 🔻 Menu déroulant */}
          {menuOpen && (
            <div className="absolute right-0 mt-2 w-40 bg-white shadow-lg rounded-lg z-50">
              <ul className="py-2">
                {!user ? (
                  <li>
                    <Link to="/login" className="block px-4 py-2 hover:bg-gray-100">🔐 Login</Link>
                  </li>
                ) : (
                  <>
                    <li><Link to="/account" className="block px-4 py-2 hover:bg-gray-100">👤 Compte</Link></li>
                    <li><Link to="/orders" className="block px-4 py-2 hover:bg-gray-100">📦 Commandes</Link></li>
                    <li><Link to="/support" className="block px-4 py-2 hover:bg-gray-100">📞 Assistance</Link></li>
                    <li><Link to="/wallet" className="block px-4 py-2 hover:bg-gray-100">💳 Portefeuille</Link></li>
                    <li><Link to="/addresses" className="block px-4 py-2 hover:bg-gray-100">🏠 Adresses</Link></li>
                    <li>
                      <button onClick={handleLogout} className="block px-4 py-2 text-red-500 hover:bg-gray-100 w-full text-left">
                        🚪 Déconnexion
                      </button>
                    </li>
                  </>
                )}
              </ul>
            </div>
          )}
        </div>
      </div>

      {/* ✅ Contenu principal */}
      <div className="mt-16 text-center p-4">
        <h2 className="text-3xl font-semibold">🛍️ Nos Produits</h2>

        {/* 🔍 Champ de recherche */}
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="p-2 border border-gray-300 rounded w-64 my-4"
        />

        {/* 📄 Lien vers la page du panier */}
        <Link to="/cart" className="bg-blue-500 text-white px-4 py-2 rounded">
          Voir mon panier 🛒 ({cart.length})
        </Link>

        {/* 📌 Liste des produits */}
        <div className="grid grid-cols-5 gap-6 mt-4">
          {filteredProducts.map((product) => (
            <div key={product._id} className="border p-4 bg-white shadow rounded-lg">
              <h3 className="text-lg font-semibold">{product.name}</h3>
              <p className="text-gray-600">Prix : {product.price}€</p>
              {product.image && <img src={product.image} alt={product.name} className="w-32 h-32 object-cover mx-auto" />}
              <button onClick={() => addToCart(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
                Ajouter au panier
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductsPage;
